import React, { FunctionComponent } from "react";
import Slider from "react-slick";

import NavigateNext from '@material-ui/icons/NavigateNext';
import NavigateBefore from '@material-ui/icons/NavigateBefore';
import Link from 'next/link';
import { keys } from "@material-ui/core/styles/createBreakpoints";

import { makeStyles } from '@material-ui/core/styles'
import GridList from '@material-ui/core/GridList'
import GridListTile from '@material-ui/core/GridListTile'
import GridListTileBar from '@material-ui/core/GridListTileBar'
import ListSubheader from '@material-ui/core/ListSubheader'
import InfoIcon from '@material-ui/icons/Info'
import PropTypes from 'prop-types'
import withStyles from '@material-ui/core/styles/withStyles'
import {
  IconButton,
  Icon,
  Tooltip,
  Button,
  Card,
  Grid,
  CardContent,
  CardActions,
  Typography
} from '@material-ui/core'
import OwlCarousel from 'react-owl-carousel2'
import a1 from './Image/a1.jpg'
import a2 from './Image/a2.jpg'
import a3 from './Image/a3.jpg'
import a4 from './Image/a4.jpg'
import a5 from './Image/a5.jpg'
import a6 from './Image/a6.jpg'
import a7 from './Image/a7.jpg'
import a8 from './Image/a8.jpg'
import a9 from './Image/a9.jpg'
import moment from 'moment'

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: '100%',
    height: '100%',
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.54)',
  },
  title: {
    padding: '5px',
    backgroundColor: '#039be5',
    color: 'white',
  },
  imgZoom: {
    transition: "transform .5s, filter 3s ease-in-out",
    filter: "grayscale(100%)",
  },
  // imgZoom: {
  //   "&:hover": {
  //     filter: "grayscale(0)",
  //     transform: "scale(1.1)",
  //     transitionDuration: "1s",
  //     transitionTimingFunction: "linear",
  //   }
  // }
})

function NextArrow(props: { className: any; onClick: any; }) {
  const { className, onClick } = props;
  return (
    <div
      className={'keyword-search__next__arrow'}
      onClick={onClick}>
      <NavigateNext />
    </div>
  );
}

function PrevArrow(props: { className: any; onClick: any; }) {
  const { className, onClick } = props;
  return (
    <div
      className={'keyword-search__prev__arrow'}
      onClick={onClick}>
      <NavigateBefore />
    </div>

  );
}


class KeywordSlide extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    const { classes } = this.props
    let settings = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 3,
      initialSlide: 0,
      nextArrow: <NextArrow className="" onClick={[]} />,
      prevArrow: <PrevArrow className="" onClick={[]} />,
      variableWidth: true,
      responsive: [
        {
          breakpoint: 1440,
          settings: {
            slidesToShow: 5,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 768,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 1,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    let dataSearch = [
      {
        title: "Giường ngủ hiện đại",
        path: "/du-an/vinhomes-central-park-pid-35872"
      },
      {
        title: "Tủ quần áo thời trang",
        path: "/du-an/vinhomes-grand-park-quan-9-pid-37584"
      },
      {
        title: "Bàn trà phòng khách hiện đại",
        path: "/du-an/vinhomes-smart-city-pid-37191"
      },
      {
        title: "Bàn trà phòng khách cổ điển",
        path: "/du-an/vinhomes-ocean-park-gia-lam-pid-37597"
      },
      {
        title: "Tủ bếp tiện nghi",
        path: "/du-an/vung-tau-pearl-pid-48674"
      },
      {
        title: "Nội thất sang trọng",
        path: "/du-an/grandeur-palace-pid-50234"
      },
      {
        title: "Giường ngủ cổ điển",
        path: "/du-an/diamond-island-pid-335"
      },
      {
        title: "Nội thất sang trọng",
        path: "/du-an/the-sun-avenue-pid-15"
      },
      {
        title: "Bàn ăn cổ điển",
        path: "/nha-dat-ban/nha-dat-ban-tai-hai-phong"
      },
      {
        title: "Bàn ăn hiện đại",
        path: "/nha-dat-ban/nha-dat-ban-tai-ha-noi"
      }
    ];

    let tileData = [
      { img: a1 },
      { img: a2 },
      { img: a3 },
      { img: a4 },
      { img: a5 },
      { img: a6 },
      { img: a7 },
      { img: a8 },
      { img: a9 },
      { img: a1 },
      { img: a2 },
      { img: a3 },
      { img: a4 },
      { img: a5 },
      { img: a6 },
      { img: a7 },
      { img: a8 },
      { img: a9 }
    ]
    const options = {
      items: 7,
      nav: false,
      rewind: false,
      autoplay: true,
      loop: true,
      autoplayTimeout: 3000,
      // autoplayHoverPause: false, // sẽ dừng lại khi chỏ chuật vào
    }
    return (
      <div className="keyword-search">
        <Typography variant='h4' style={{ textAlign: 'center' }} color='primary'>
          Khách hàng - đối tác
      </Typography>
        <Slider {...settings}>
          {/* {
          dataSearch.map((searchObject: any, index: number) => {
            return (
              <div key={index}>
                <Link href={searchObject.path} as={searchObject.path}>
                  <a>
                    <div
                      style={{ backgroundColor: "#1976d2", color: 'white', fontSize: 16, fontWeight: 500 }}
                      className="keyword-search__item"
                    >{searchObject.title}
                    </div>
                  </a>
                </Link>
              </div>
            );
          })
        } */}
          {
            tileData.map((element, index) => {
              return (
                <div key={index} style={{ backgroundColor: 'white' }} className={classes.imgZoom}>
                  <CardContent>
                    <img src={element.img} alt="Nội thất Dodo" />
                  </CardContent>
                </div>
              )
            })
          }
        </Slider>
      </div>
    );
  }
};

KeywordSlide.propTypes = {
  classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(KeywordSlide);
