import gql from "graphql-tag";

const FOLLOW_CHANNEL = gql`
  mutation channelFollow ($input: ChannelFollowInput!) {
    channelFollow(input: $input) {
      id
      follow_value
    } 
  }
`;

const UNFOLLOW_CHANNEL = gql`
  mutation channelUnfollow ($input: ChannelFollowInput!) {
    channelUnfollow(input: $input) {
      id
      follow_value
    } 
  }
`;


export {
  FOLLOW_CHANNEL,
  UNFOLLOW_CHANNEL
}
