import React, { FunctionComponent } from 'react';
import {makeStyles} from '@material-ui/core/styles'
import Info from './Tabs/Info'
import Home from './Tabs/Home'
import Work from './Tabs/Work'
import Video from './Tabs/Video'
import Introduce from './Tabs/Info'
import Comments from "./Tabs/Comments"
import Ratings from "./Tabs/Ratings"
import AdSellLeases from "./Tabs/AdSellLeases"
import Header from "./Header"
import Paper from '@material-ui/core/Paper';
import {
  Tabs, Tab, Divider, colors, Grid,
  Button, Icon, IconButton, Typography,
  Card, CardContent, CardActions,
  Avatar
} from '@material-ui/core';
import Page from "./Page";
 
const useStyles = makeStyles(theme => ({
  root: {},
  rootTabs: {
    flexGrow: 1,
  },
  large: {
    width: theme.spacing(7),
    height: theme.spacing(7),
  },
  inner: {
    padding: theme.spacing(3)
  },
  divider: {
    backgroundColor: colors.grey[300]
  },
  content: {
    marginTop: theme.spacing(3)
  }
}));

interface IndexProps {
  comments: CommentsProp[],
  infoUser: UserProps,
  ratings: RatingsProp[],
}

interface CommentsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface UserProps {
  address: string
  avatar: string
  birthday: string
  email: string
  facebook: string
  first_name: string
  gender: string
  google_id: string
  intro: string
  id: string
  last_name: string
  phone: string
  mobile: string
  skype: string
  username: string
  viber: string
  website: string
  zalo: string
  created_at: string
  updated_at: string
}

interface RatingsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    rate: number,
    like_number: string,
    created_at: string,
  },
}

const Index: FunctionComponent<IndexProps> = (props: IndexProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = React.useState("home")
  const tabs = [
    { value: 'home', label: 'Tổng quan'},
    { value: 'info', label: 'Thông tin'},
    { value: 'ad', label: 'Tin rao'},
    { value: 'work', label: "Hoạt động"},
    { value: "video", label: "Video"},
    { value: "ratings", label: "Đánh giá"},
    { value: "introduce", label: "Giới thiệu"},
  ]

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value)
  }

  const relatedChannel = [
    { title: "Mua bán bất động sản", follow: "23.365", image: "https://image.plo.vn/w653/Uploaded/2020/ongqbatfnacy2/2016_06_09/download_isam.jpg" },
    { title: "Thiết kế thi công nhà ở", follow: "45.365", image: "https://media.ngoisao.vn/news/2018/08/14/phong-thuy10-ngoisao.vn-w580-h387.jpg" },
    { title: "Thuê văn phòng giá rẻ",  follow: "28.365", image: "https://cafefcdn.com/thumb_w/650/2017/du-an-vincity-tay-mo-dai-mo-1489197061940.jpg" },
    { title: "Cho thuê bất động sản",  follow: "108.365", image: "https://phongthuytamnguyen.com/uploads/images/2018_06_16_fea1a1c344.jpeg" },
    { title: "Biệt thư cao cấp",  follow: "253.365", image: "https://media.ex-cdn.com/EXP/media.phunutoday.vn/files/upload_images/2015/07/15/phpng-thuy.jpg" },
    { title: "Thuê chung cư giá rẻ",  follow: "122.365", image: "https://vneconomy.mediacdn.vn/zoom/480_270/2019/7/24/vincity-1563949484824992585411-crop-15639494893311384564187.jpeg" },
    { title: "Trao đổi thông tin BĐS", follow: "18.365", image: "https://thumb-reatimes.cdn.vccloud.vn/ha_z/500_350/https@@$$media1-reatimes.cdn.vccloud.vn/upload/Daz8bLLgWiyONlYhLXFY7g/files/hinh-27-1569202405931654447220-crop-1573190046626669111417(1).jpg" },
    { title: "Khu thương mại cao ốc",  follow: "45.365", image: "https://land24h.net/wp-content/uploads/2018/05/chung-cu-green-stars.jpg" },
  ]

  return (
      <Page title="Profile">
        <Header infoUser={props.infoUser}/>
        <CardContent>
          <Grid container spacing={10}>
            <Grid item xs={8}>
              <div className={classes.inner}>
                <Paper square className={classes.rootTabs}>
                <Tabs
                  onChange={handleTabsChange}
                  scrollButtons="auto"
                  indicatorColor="primary"
                  textColor="primary"
                  value={itemTab}
                >
                  {tabs.map(tab => (
                    <Tab key={tab.value} label={tab.label} value={tab.value} />
                  ))}
                </Tabs>
                </Paper>
                <Divider className={classes.divider}/>
                <div className={classes.content}>
                  {itemTab === 'home' && <Home infoUser={props.infoUser}/>}
                  {itemTab === 'work' && <Work infoUser={props.infoUser}/>}
                  {itemTab === 'video' && <Video infoUser={props.infoUser}/>}
                  {itemTab === 'introduce' && <Introduce infoUser={props.infoUser}/>}
                  {itemTab === 'info' && <Info infoUser={props.infoUser}/>}
                  {itemTab === 'ad' && <AdSellLeases userId={props.infoUser.id}/>}
                  {itemTab === 'ratings' && <Ratings ratings={props.ratings} userId={props.infoUser.id}/>}
                </div>
              </div>
            </Grid>
            <Grid item xs={4}>
              <Typography color="primary">
                Kênh liên quan
              </Typography>
              {relatedChannel.map((item: any, index: number) => {
                return (
                  <Grid key={index} container spacing={2}>
                    <Grid item xs={12}>
                      <Avatar src={item.image} className={classes.large} alt="" style={{ display: "inline" }}/>
                      <Typography style={{ display: "inline" }}>
                        {item.title}
                      </Typography>
                      <Typography>
                        {item.follow}
                      </Typography>
                    </Grid>
                  </Grid>
                )
              })}
              <Grid container spacing={2}>
                <Button fullWidth >Xem thêm</Button>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Page>
  )
}

export default Index;
