import React from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField, Button, Icon, IconButton} from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import {apolloClient} from "../../../utils"
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import {GET_AD_SELL_LEASE_COMMENTS, GET_AD_SELL_LEASE_RATINGS} from "../../../graphql/reviews/queries";
import {GET_USER_BY_ID } from "../../../graphql/user/queries/index"
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

const useStyles = makeStyles(theme => ({
  root: {
  },
  iconUp: {
    fontSize: "10",
  },
  subheader: {
    flexWrap: 'wrap',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

interface Ratings {
  ratings: { data: { adSellLeaseRatings: { edges: RatingsProps[]}} },
}

interface RatingsProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface OverallReviewsProps {
  className?:string,
  ratings: RatingsProps[],
  userId: string
} 

const Ratings: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  const { className } = props;
  const classes = useStyles();
  const [page, setPage] = React.useState<number>(2)
  const [listMore, setListMore] = React.useState<Ratings[]>([])
  const setViewPage = () => {
    let dataFilter = JSON.stringify({ id: props.userId });
    apolloClient.query({query: GET_AD_SELL_LEASE_RATINGS, variables: {
        "page": page,
        "limit": 10,
        "filter": dataFilter
      }}).then(response => {
        setListMore(listMore.concat(response.data.adSellLeaseRatings.edges))
    });
    setPage(page + 1)
  }
  return (
    <div  className={clsx(classes.root, className)}>
      {props.ratings.length 
        ? props.ratings.map((item: any, index: number) => {
          return (
            <Grid container spacing={4} key={index}>
              <Grid item xs={12}>
                <Card>
                  <CardHeader 
                    avatar={
                      <Avatar alt="Reviewer" src={item.node.user.avatar} >
                        {item.node.user.first_name}  {item.node.user.last_name}
                      </Avatar>
                    }
                    disableTypography
                    subheader={
                      <div className={classes.subheader}>
                        <Typography variant="body2">
                          Bình luận bởi{' '} <b>{item.node.user.first_name} {item.node.user.last_name} </b> 
                        </Typography>
                        <Typography variant="body2">
                          {moment(Number(item.node.created_at)).fromNow()}
                        </Typography>
                        {/* <Typography variant="body2">
                          Dự án :  
                        </Typography> */}
                      </div>
                    }
                  />
                  <div className={classes.message}>
                    <Typography variant="subtitle2">{item.node.content}</Typography>
                  </div>
                </Card>
              </Grid>
            </Grid>
          )  
        }) 
        : "Hiện tại không có đánh giá nào"
      }
      {listMore.map((item: any, index: number) => {
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={item.node.user.avatar} >
                      {item.node.user.first_name}  {item.node.user.last_name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      <Typography variant="body2">
                        Bình luận bởi{' '} <b>{item.node.user.first_name} {item.node.user.last_name} </b> 
                      </Typography>
                      <Typography variant="body2">
                        {moment(Number(item.node.created_at)).fromNow()}
                      </Typography>
                      {/* <Typography variant="body2">
                        Dự án :  
                      </Typography> */}
                    </div>
                  }
                />
                <div className={classes.message}>
                  <Typography variant="subtitle2">{item.node.content}</Typography>
                </div>
              </Card>
            </Grid>
          </Grid>
        )  
      })}
      <Grid container spacing={4}>
        <Grid item>
          { props.ratings.length >= 15 ? <Button onClick={setViewPage} >Hiển thị thêm</Button> : "" }
        </Grid>
      </Grid>
    </div>
  );
};


export default Ratings;
