import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Skeleton from '@material-ui/lab/Skeleton';
 
const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
    margin: theme.spacing(2),
  },
  media: {
    height: 190,
  },
}))

interface MediaProps {
  loading?: boolean;
  listad: AdProps,
}
 
interface AdProps  {
  node: {
    name: string,
    id: string,
    title: string,
    seo_title: string,
    created_at: string,
    updated_at: string,
    slug: string,
    project: string,
    length:  string,
    width: string,
    floor_number: string,
    living_room_number: 0
  }[],
}


const ListMedia: React.FunctionComponent<MediaProps> = (props: MediaProps) => {
  const { loading = false } = props;
  const classes = useStyles();
  return ( 
    <div>
      {props.listad.map((item: any, index: number) => {
        <Card className={classes.card} key={index}>
         <CardHeader
           avatar={<Avatar alt="Ted talk" src="https://pbs.twimg.com/profile_images/877631054525472768/Xp5FAPD5_reasonably_small.jpg" />}
           action={<IconButton aria-label="settings"> <MoreVertIcon /> </IconButton>}
           title={'Ted'}
           subheader={'5 hours ago'}
         />
         <CardMedia 
           className={classes.media} title="Ted talk"
           image="https://pi.tedcdn.com/r/talkstar-photos.s3.amazonaws.com/uploads/72bda89f-9bbf-4685-910a-2f151c4f3a8a/NicolaSturgeon_2019T-embed.jpg?w=512"
         />
         <CardContent>
           <Typography variant="body2" color="textSecondary" component="p">
             Why First Minister of Scotland Nicola Sturgeon thinks GDP is the wrong measure of a country's success:
           </Typography>
         </CardContent>
       </Card>
      })}
    </div>
   
  )
}

export default ListMedia;
