import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography } from '@material-ui/core';
import StarIcon from '@material-ui/icons/Star';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import Rating from '@material-ui/lab/Rating';
import Box from '@material-ui/core/Box';


const useStyles = makeStyles(theme => ({
  root: {},
  posts: {
    marginTop: theme.spacing(2)
  },
  post: {
    marginBottom: theme.spacing(2)
  },
  stars: {
    display: 'flex',
    alignItems: 'center'
  },
  rating: {
    marginLeft: theme.spacing(2),
    fontWeight: theme.typography.fontWeightBold
  },
  starIcon: {
    fontSize: 18,
    height: 18,
    width: 18,

  },
  starFilledIcon: {
    color: "red"
  },
  starBorderIcon: {
    color: theme.palette.primary.main
  }
}));

interface OverallReviewsProps {
  className?:string,
}
 
interface OverallReviewsProps {
  className?:string,
}

const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  let reviews = [
    {
      id: "1",
      reviewer: {
        avatar: "https://zicxa.com/hinh-anh/wp-content/uploads/2019/07/T%E1%BB%95ng-h%E1%BB%A3p-h%C3%ACnh-%E1%BA%A3nh-g%C3%A1i-xinh-d%E1%BB%85-th%C6%B0%C6%A1ng-cute-nh%E1%BA%A5t-6.jpg",
        name: "Nguyễn Hồng Thúy"
      },
    rating: "rating",
    created_at: "2020-31-03",
    project: {
      title: "project title",
      price: "number price",
    },
    message: "message",
    currency: "currency",
    pricePerHour: 2,
    hours: 4,
    }, {
      id: "1",
      reviewer: {
        avatar: "https://thuthuatnhanh.com/wp-content/uploads/2019/07/anh-girl-xinh-facebook-tuyet-dep-387x580.jpg",
        name: "Mai Trâm"
      },
    rating: "rating",
    created_at: "2020-31-03",
    project: {
      title: "project title",
      price: "number price",
    },
    message: "message",
    currency: "currency",
    pricePerHour: 2,
    hours: 4,
    }
  ]
  const [value, setValue] = React.useState<number | null>(2);
  let ratings= reviews.map(review => review.rating)
  const { className, ...rest } = props;
  let rating = 0;
  if (ratings.length > 0) {
      rating = ratings.reduce((prev:any, current:any) => prev + current, 0) / ratings.length;
  }
  const classes = useStyles();
  return (
    <div {...rest} className={clsx(classes.root, className)}>
      <Card {...rest} className={clsx(classes.root, className)}>
        <CardContent>
          <Grid alignItems="center" container spacing={3}>
            <Grid item>
              <Typography variant="h5">Overall Reviews</Typography>
            </Grid>
            <Grid className={classes.stars} item >
              <div>
                <Box component="fieldset" mb={3} borderColor="transparent">
                  <Rating
                    name="simple-controlled"
                    value={value}
                    onChange={(event, newValue) => {
                      setValue(newValue);
                    }}
                  />
                </Box>
              </div>
              <Typography className={classes.rating} variant="h6">{rating}</Typography>
            </Grid>
            <Grid item>
              <Typography color="textSecondary" variant="body2" >
                {ratings.length} reviews in total
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </div>
  );
};


export default Project;
