import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import InfoIcon from '@material-ui/icons/Info';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import { I18n } from 'react-redux-i18n';
import {
    IconButton,
    Icon,
    Tooltip,
    Button,
    Card,
    Grid,
    CardContent,
    CardActions,
    Typography,
    AppBar,
    Toolbar,
    Hidden,
    Popover,

} from '@material-ui/core';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { titles } from '../../constants/index';
import GamepadIcon from '@material-ui/icons/Gamepad';
import moment from 'moment';
import _ from 'lodash';

const useStyles = makeStyles((theme) => ({
    listItem: {
        backgroundColor: '#e3f2fd',
        "&:hover": {
            // backgroundColor: '#90caf9',
        }
    },
    title: {
        display: 'inline',
        cursor: 'default',
        color: 'white',
        fontWeight: 400,
        fontSize: 16,
        padding: '0 12px',
        // borderLeft: '2px solid white',
    },
    header: {
        height: 40,
        width: '100%',
        backgroundColor: '#1976d2'
    },
    rootPopover: {
        pointerEvents: 'none',
        marginTop: 10,
    },
    divider: {
        height: 28,
        margin: 4,
        color: 'white',
        backgroundColor: 'white',
        display: 'inline',
    },

}));


const Actions = (props) => {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [indexOpen, setIndex] = React.useState(null);

    const popoverOpen = (event, index) => {
        setOpen(event.currentTarget);
        setIndex(index);
    };

    const popoverClose = () => {
        setOpen(null);
        setIndex(null);
    };

    return (
        <Grid container spacing={0} className={classes.header} alignItems='center' justify='center' direction='row' >
            <Grid item xs={2}>
                <Typography color='inherit' style={{ color: 'white', fontWeight: 500 }} >
                    0377 535 717
                </Typography>
            </Grid>
            <Grid item xs={8}>
                <div style={{ display: 'inline-block', color: 'white' }}>

                </div>
                {titles.map((item, index) => {
                    const { elements } = item;
                    return (
                        <div style={{ display: 'inline-block' }}>
                            <Typography
                                key={index}
                                className={classes.title}
                                onMouseEnter={(event) => popoverOpen(event, index)}
                            // onMouseLeave={popoverClose}
                            >
                                {item.title}
                            </Typography>

                            {indexOpen === index && <Popover
                                id={"mouse-over-popover" + index}
                                className={classes.rootPopover}
                                open={open}
                                anchorEl={open}
                                // Vị trí ghim, Theo chieu doc, theo chieu ngang
                                anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
                                // Biến đổi
                                transformOrigin={{ vertical: 'top', horizontal: 'bottom' }}
                                onClose={popoverClose}
                                disableRestoreFocus
                            >
                                {elements.map((element, count) => {
                                    return (
                                        <List component="nav" className={classes.listItem} aria-labelledby="nested-list-subheader">
                                            <ListItem button>
                                                {element.element}
                                            </ListItem>
                                            {count + 1 !== item.elements.length && <Divider />}
                                        </List>
                                    )
                                }
                                )}
                            </Popover>}
                        </div>
                    )
                })}
            </Grid>
            <Grid item xs={2}>
                <Typography color='inherit' style={{ color: 'white', fontWeight: 500 }} >
                    0377 535 717
                </Typography>
            </Grid>
        </Grid>
    )
};

export default Actions;
