import React, {useEffect} from 'react';
import {NextPage} from "next";

//Import Components
import Layout from '../../components/Layout/Layout';
import BannerSlider from "../../components/PageDetail/BannerSlider";
import Main from "../../components/Project/ProjectMain";

//Import Const
import {apolloClient} from "../../utils";
import {GET_PROJECT_BY_SLUG} from "../../graphql/project/queries";
import {GET_INVESTOR_BY_ID} from "../../graphql/investor/queries"

//Import Module
import StringProcess from "../../modules/stringProcess";

//Import SCSS
import "../../assets/scss/style.scss";
import {localStorageCompareKey, mainURL} from "../../constants";
import urlProcess from "../../modules/urlProcess";
import Validator from "../../modules/validator";
import {COUNT_VIEW_PROJECT} from "../../graphql/project/mutation";

interface ProjectItemProp {
  id: string,
  investor: string,
  price: string,
  name: string,
  land_area: string,
  image: ImageProp[],
  position_content: string,
  infrastructure_content: string,
  seo_title: string|null,
  seo_description: string|null,
  address: { text: string, ward: string, district: string, city: string },
  cover_image: string,
  intro_content: string
  created_at: string
  updated_at: string
  view_count: number
  google_map_latitude: string
  google_map_longitude: string
}
interface ImageProp { 
  alt_text: string 
  description: string
  title: string
  src: string
}
interface ProjectDetails {
  url: { query: { slug: string }},
  projectDetail: { data: { project: ProjectItemProp } },
  slug: string
}
interface QueryProp { query: { slug: string } }
interface CompareDataProps {
  avatar: string,
  title: string|null,
  id: string,
  type: string
}

interface DetailInvestor {
  id: string
  name: string
  description: string
  slug: string
  email: string
  website: string
  image: string
}
 


// @ts-ignore
const ProjectDetailPage: NextPage<ProjectDetails> = (props: ProjectDetails) => {
  let title = (props.projectDetail.data.project.name)?props.projectDetail.data.project.name:'Xem chi tiết dự án';
  let albumImage = props.projectDetail.data.project.image;
  let priceValue = (props.projectDetail.data.project.price)?StringProcess.addCommas(props.projectDetail.data.project.price) + 'triệu/m2':'Liên hệ';
 
  let typeProject = '';
  let statusProject = '';
  let yearBuildProject = '';

  let square = (<>m<sup>2</sup></>);
  let areaString = (
    <>
      {StringProcess.addCommas(props.projectDetail.data.project.land_area)} {square}
    </>
  );

  let imageAvatar = 'https://static.nhadatmoi.net/default/home_default.jpg';
  if (props.projectDetail.data.project.cover_image) {
    imageAvatar = urlProcess.processImageUrl(props.projectDetail.data.project.cover_image, 'project');
  }

  const freshText = (input: string) => {
    input = StringProcess.stripProperties(input, []);
    input = StringProcess.stripTags(input, '');
    input = input.replace(/&nbsp;/g, ' ');
    return input;
  };
  const convertDescription = (input: string) => {
    return input.replace(/&#(\d+);/g, function(match, dec) {
      return String.fromCharCode(dec);
    });
  };

  let metaTitle = '';
  let metaDescription = '';
  if (props.projectDetail.data.project.seo_title) {
    metaTitle = props.projectDetail.data.project.seo_title;
  }
  if (props.projectDetail.data.project.seo_description) {
    metaDescription = props.projectDetail.data.project.seo_description;
  }
  if (metaTitle === '' || !metaTitle) {
    metaTitle = title;
  }
  if (metaDescription === '' || !metaDescription) {
    metaDescription = freshText(props.projectDetail.data.project.intro_content);
    metaDescription = convertDescription(metaDescription);
    metaDescription = StringProcess.truncateString(metaDescription, 255, '');
  }
  let initStateInvestor = {
    id: "",
    name: "",
    description: "",
    slug: "",
    email: "",
    website: "",
    image: "",
  }
  //Count Ad Sell Lease
  const [detailInvestor, setDetailInvestor] = React.useState<DetailInvestor>(initStateInvestor);
  
  useEffect(() => {
    let projectId = props.projectDetail.data.project.id;
    apolloClient.mutate({mutation: COUNT_VIEW_PROJECT, variables: {
        "input": {
          "id": projectId
        }
      }}).then((response: any) => {
    });
    let investorId = props.projectDetail.data.project.investor
    apolloClient.query({query: GET_INVESTOR_BY_ID, variables: {
      "id": investorId
      }}).then(response => {
        if (!Validator.isBlank(response.data) && !Validator.isBlank(response.data.investor)) {
          setDetailInvestor(response.data.investor);
        }
    });
  }, []);

  const [dataCompare, setDataCompare] = React.useState({});
  let updateCompareData = (data: CompareDataProps) => {
    let dataToCompare = [];
    let isAddToLocal = true;
    if (localStorage.getItem(localStorageCompareKey) && !Validator.isBlank(localStorage.getItem(localStorageCompareKey))) {
      dataToCompare = JSON.parse(localStorage.getItem(localStorageCompareKey) as string);
      for (let index in dataToCompare) {
        if (dataToCompare[index].id === data.id) {
          isAddToLocal = false;
        }
        if (dataToCompare[index].type !== data.type) {
          localStorage.removeItem(localStorageCompareKey);
          dataToCompare = [];
          break;
        }
      }
    }
    if (isAddToLocal) {
      dataToCompare.push(data);
      // @ts-ignore
      setDataCompare(dataToCompare);
      localStorage.setItem(localStorageCompareKey, JSON.stringify(dataToCompare));
    }
  };
  let propertyOverview = {
    id: props.projectDetail.data.project.id,
    building_density: '',
    type: typeProject,
    status: statusProject,
    floor_number: 0,
    living_room_number: 0,
    bed_room_number: 0,
    toilet_room_number: 0,
    gara_number: 0,
    material: '',
    length: '',
    width: '',
    price: priceValue,
    area: areaString,
    year_built: yearBuildProject,
    address: props.projectDetail.data.project.address,
    size: '-',
    description: props.projectDetail.data.project.intro_content,
    map: {
      lat: props.projectDetail.data.project.google_map_latitude,
      lng: props.projectDetail.data.project.google_map_longitude
    },
    title: title,
    view_count: props.projectDetail.data.project.view_count,
    updated_at: props.projectDetail.data.project.updated_at,
    created_at: props.projectDetail.data.project.created_at,
    position_content: props.projectDetail.data.project.position_content,
    infrastructure_content: props.projectDetail.data.project.infrastructure_content
  };
  let contactDefault = {
    image: '',
    name: '',
    address: '',
    mobile: '',
    phone: '',
    email: ''
  };
  
  console.log( "\nInvestor", detailInvestor)
  return (
    <Layout
      title={metaTitle}
      description={metaDescription}
      {...props}
      image={imageAvatar}
      canonical={ mainURL + '/du-an/' + props.slug}
      path={'du-an'}
      dataCompare
      ={dataCompare}
    >
      <BannerSlider images={albumImage} imageType='project'/>
      <Main
        propertyOverview={propertyOverview}
        images={albumImage}
        contact={contactDefault}
        imageType='project'
        detailInvestor={detailInvestor}
        objectUrl={ mainURL + '/du-an/' + props.slug}
        avatar={props.projectDetail.data.project.cover_image}
        updateCompareData={(data: CompareDataProps) => updateCompareData(data)}
      />
    </Layout>
  );
};

// @ts-ignore
ProjectDetailPage.getInitialProps = async function(props: QueryProp) {
  let projectSlug = props.query.slug;
  let projectDetail = await apolloClient.query({query: GET_PROJECT_BY_SLUG, variables: {
      "slug": projectSlug
    }}).then(response => {
    return response;
  });
  return {
    projectDetail: projectDetail,
    slug: projectSlug,
  };
};

export default ProjectDetailPage;
