import React, { FunctionComponent } from "react";
import Slider from "react-slick";

import NavigateNext from '@material-ui/icons/NavigateNext';
import NavigateBefore from '@material-ui/icons/NavigateBefore';
import Link from 'next/link';
import { keys } from "@material-ui/core/styles/createBreakpoints";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  keyWordSearch: {
    width: '100%',
    float: 'left',
    padding: '0 30px 0 30px',
    margin: '0 0 50px 0',
  },
  keyWordSearchHeading: {
    width: '100%',
    fontFamily: 'Roboto sans-serif',
    fontWeight: 900,
    fontSize: '20px',
    color: 'red',
    margin: '10px 0 20px 0',
  },
  keyWordSearchItem: {
    fontSize: '14px',
    fontFamily: 'Roboto sans-serif',
    fontWeight: 700,
    color: '#444',
    borderRadius: '4px',
    outline: 'none',
    opacity: 0.8,
    height: '50px',
    lineHeight: '50px',
    paddingLeft: '20px',
    paddingRight: '20px',
    background: '#e0e0e0',
    cursor: 'pointer',
  }
}));

function NextArrow(props) {
  const { onClick } = props;
  return (
    <div
      className={'keyword-search__next__arrow'}
      onClick={onClick}>
      <NavigateNext />
    </div>
  );
}

function PrevArrow(props) {
  const { onClick } = props;
  return (
    <div
      className={'keyword-search__prev__arrow'}
      onClick={onClick}>
      <NavigateBefore />
    </div>

  );
}

let dataSearch = [
  {
    title: "Giường ngủ hiện đại",
    path: "/du-an/vinhomes-central-park-pid-35872"
  },
  {
    title: "Tủ quần áo thời trang",
    path: "/du-an/vinhomes-grand-park-quan-9-pid-37584"
  },
  {
    title: "Bàn trà phòng khách hiện đại",
    path: "/du-an/vinhomes-smart-city-pid-37191"
  },
  {
    title: "Bàn trà phòng khách cổ điển",
    path: "/du-an/vinhomes-ocean-park-gia-lam-pid-37597"
  },
  {
    title: "Tủ bếp tiện nghi",
    path: "/du-an/vung-tau-pearl-pid-48674"
  },
  {
    title: "Nội thất sang trọng",
    path: "/du-an/grandeur-palace-pid-50234"
  },
  {
    title: "Giường ngủ cổ điển",
    path: "/du-an/diamond-island-pid-335"
  },
  {
    title: "Nội thất sang trọng",
    path: "/du-an/the-sun-avenue-pid-15"
  },
  {
    title: "Bàn ăn cổ điển",
    path: "/nha-dat-ban/nha-dat-ban-tai-hai-phong"
  },
  {
    title: "Bàn ăn hiện đại",
    path: "/nha-dat-ban/nha-dat-ban-tai-ha-noi"
  }
];

let settings = {
  dots: false,
  infinite: true,
  speed: 500,
  slidesToShow: 5,
  slidesToScroll: 3,
  initialSlide: 0,
  nextArrow: <NextArrow onClick={[]} />,
  prevArrow: <PrevArrow onClick={[]} />,
  variableWidth: true,
  responsive: [
    {
      breakpoint: 1440,
      settings: {
        slidesToShow: 5,
        slidesToScroll: 3,
        infinite: true,
        dots: true
      }
    },
    {
      breakpoint: 768,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
        initialSlide: 2
      }
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1
      }
    }
  ]
};

const KeywordSlide = (props) => {
  const classes = useStyles();

  return (
    <div className={classes.keyWordSearch}>
      <h3
        // className={classes.keyWordSearchHeading}
        className='keyword_search__heading'
      >Từ khóa tìm kiếm HOT</h3>
      <Slider {...settings}>
        {
          dataSearch.map((searchObject, index) => {
            return (
              <div key={index}>
                <Link href={searchObject.path} as={searchObject.path}>
                  <a>
                    <div
                      style={{ backgroundColor: "#1976d2", color: 'white', fontSize: 16, fontWeight: 500 }}
                      // className={classes.keyWordSearchItem}
                      className='keyword_search__item'
                    >{searchObject.title}
                    </div>
                  </a>
                </Link>
              </div>
            );
          })
        }
      </Slider>
    </div >
  );
};

export default KeywordSlide
