import React from 'react';
import { makeStyles, ThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import {
    List,
    Divider,
    ListItemIcon,
    ListItem,
    ListItemText,
    Collapse,
    Button,
    ListSubheader,
    Popover,
    Typography,
    MenuItem,

} from '@material-ui/core';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import GamepadIcon from '@material-ui/icons/Gamepad';
import { listMenu } from '../../constants/index';

const useStyles = makeStyles((theme) => ({
    listItem: {
        backgroundColor: '#e3f2fd',
        "&:hover": {
            // backgroundColor: '#90caf9',
        }
    },
    textItem: {
        color: '#1976d2',
        fontSize: 14,
    },
    popover: {
        pointerEvents: 'none', // none
    },
}));

const themeListText = createMuiTheme({
    overrides: {
        MuiTypography: {
            fontSize: '10px'
        },
    }
});

function NestedList() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [indexMenu, setIndexMenu] = React.useState(null);
    const [countMenu, setCountMenu] = React.useState(null);
    const handlePopoverOpen = (event, index) => {
        setAnchorEl(event.currentTarget);
        setOpen(true);
        setCountMenu(0);
        setIndexMenu(index);
    };

    const handlePopoverClose = () => {
        setAnchorEl(null);
        setOpen(false);
        setIndexMenu(null);
    };

    const setHoverMenu = (count) => {
        setCountMenu(count)
    }
    const id = open ? indexMenu : undefined;
    return (
        <List
            component="nav"
            aria-labelledby="nested-list-subheader"
            // className={classes.root}
            style={{ padding: 0 }}
        >
            {listMenu.map((item, index) => {
                const { rooms } = item
                return (
                    <div key={index}>
                        <ListItem
                            className={classes.listItem} button id={id}
                            onMouseEnter={(event) => handlePopoverOpen(event, index)}
                            style={{ backgroundColor: countMenu === index ? '#90caf9' : '#e3f2fd', height: 35, padding: '0 4px' }}
                        >
                            <GamepadIcon style={{ fontSize: 25, color: '#1976d2', padding: 4 }} />
                            {/* <ThemeProvider theme={themeListText}>
                                <ListItemText primary={`${item.title}`} className={classes.textItem} />
                            </ThemeProvider> */}
                            <Typography className={classes.textItem}>
                                {item.title}
                            </Typography>
                        </ListItem>
                        <Divider />
                        {indexMenu === index &&
                            <Popover
                                id={id}
                                className={classes.popover}
                                open={open}
                                anchorEl={anchorEl}
                                anchorOrigin={{ vertical: 'right', horizontal: 'right' }} // Ghim
                                transformOrigin={{ vertical: 'top', horizontal: 'left' }} // Vi tri hien thi
                                onMouseLeave={handlePopoverClose}
                            // onMouseOut={handlePopoverClose}
                            >
                                <List
                                    component="nav"
                                    aria-labelledby="nested-list-subheader"
                                    style={{ padding: 0, pointerEvents: 'auto', corsor: 'pointer' }}
                                >
                                    {
                                        rooms.map((element, count) => {
                                            return <div key={count}>
                                                <ListItem
                                                    autoFocus={true}
                                                    button={true}
                                                    onClick={(count) => setHoverMenu(count)}
                                                    style={{ backgroundColor: countMenu === count ? '#90caf9' : '#e3f2fd', height: 35, padding: '0 4px' }}
                                                    onMouseEnter={() => setHoverMenu(count)}
                                                >
                                                    <GamepadIcon style={{ fontSize: 25, color: '#1976d2', padding: 4 }} />
                                                    <ListItemText primary={`${element.room}`} style={{ color: '#1976d2' }} />
                                                </ListItem >
                                                <Divider />
                                            </div>
                                        })
                                    }
                                </List>
                            </Popover>
                        }
                    </div>
                );
            })}
        </List >
    );
}

export default NestedList;
