import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { 
  qualityTitle,
  extraQualityTitle,
  toturialTitle,
  toturialContent,
  toolTitle,
  toolContent,
  warrantTitle,
  warrantContent,
  integratedTitle,
  integratedContent,
  integratedContentone,
  integratedContentTwo,
  postEasyTitle,
  postEasyContent,
  controlTitle,
  controlContent,
  priceListTitle,
  priceListContent,

  qualitys,
} from "./Constants"
const Icon01 = require('../../assets/images/post-icon-01.svg');
const Icon02 = require('../../assets/images/post-icon-02.svg');
const Icon03 = require('../../assets/images/post-icon-03.svg');
const Icon04 = require('../../assets/images/post-icon-04.svg');
const Icon05 = require('../../assets/images/post-icon-05.svg');

const Benefit = () => {
  return (
    <div className={"mission-area body-general"} >
      <Typography variant="h4" style={{ fontWeight: 700 }} >
        {qualityTitle}
      </Typography>
      <Typography style={{ fontWeight: 500 }} >
        {extraQualityTitle}
      </Typography>
      {/* <XX /> */}
      <Grid container alignItems="flex-start" direction="row" justify="center" spacing={10}>
        {qualitys.map((element: any, index: number) => {
          return (
            <Grid item xs={4}>
              <div style={{ textAlign: 'center' }} >
                <img src={element.icon} style={{ width: 100, height: 120, padding: "12px" }} />
              </div>
              <Typography variant="h6" style={{ fontWeight: 500, textAlign: 'center'}}>
                {element.title}
              </Typography>
              <Typography variant="h6">
                {element.content.map((e: any, count: number) => {
                  if(count === 0){
                    return <Typography>{e.item}</Typography>
                  } else {
                    return <Typography><li>{e.item}</li></Typography> 
                  }
                })}
              </Typography>
            </Grid>
          )
        })}
      </Grid>
     
    </div>    
  );
};

export default Benefit;
