import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {
    List,
    Divider,
    ListItemIcon,
    ListItem,
    ListItemText,
    Collapse,
    ListSubheader,
    Popover,
    Typography,

} from '@material-ui/core';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import GamepadIcon from '@material-ui/icons/Gamepad';
import { listMenu } from '../../constants/index';

const useStyles = makeStyles((theme) => ({
    listItem: {
        backgroundColor: '#e3f2fd',
        "&:hover": {
            // filter: "grayscale(0)",
            backgroundColor: '#90caf9',
            // transitionTimingFunction: "linear",
        }
    },
    textItem: {
        color: '#1976d2',
    },
    root: {
        width: '100%',
    },
    nested: {
        paddingLeft: theme.spacing(4),
    },
    popover: {
        pointerEvents: 'none',
    },
}));

export default function NestedList() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [indexMenu, setIndexMenu] = React.useState(null);

    const handlePopoverOpen = (event, index) => {
        setAnchorEl(event.currentTarget);
        setOpen(true);
        setIndexMenu(index);
    };

    const handlePopoverClose = () => {
        console.log('handlePopoverClose');
        setAnchorEl(null);
        setOpen(false);
        setIndexMenu(null);
    };
    const onClose = () => {
        setOpen(false);
    }
    const id = open ? indexMenu : undefined;
    return (
        <List
            component="nav"
            aria-labelledby="nested-list-subheader"
            className={classes.root}
            style={{ padding: 0 }}
        >
            {listMenu.map((item, index) => {
                const { rooms } = item
                return (
                    <div key={index}>
                        <ListItem
                            className={classes.listItem} button id={id}
                            onMouseEnter={(event) => handlePopoverOpen(event, index)}
                            style={{ backgroundColor: indexMenu === index ? '#90caf9' : '#e3f2fd' }}
                        >
                            <GamepadIcon style={{ fontSize: 30, color: '#1976d2', padding: 6 }} />
                            <ListItemText primary={`${item.title}`} className={classes.textItem} />
                        </ListItem>
                        <Divider />
                        {indexMenu === index &&
                            <Popover
                                id={id}
                                className={classes.popover}
                                open={open}
                                anchorEl={anchorEl}
                                anchorOrigin={{ vertical: 'right', horizontal: 'right' }} // Ghim
                                transformOrigin={{ vertical: 'top', horizontal: 'left' }} // Vi tri hien thi
                                disableRestoreFocus
                            >
                                <List
                                    component="nav"
                                    aria-labelledby="nested-list-subheader"
                                    className={classes.root}
                                    style={{ padding: 0 }}
                                >
                                    {
                                        rooms.map((element, count) => {
                                            return (
                                                <div key={count}>
                                                    <ListItem button key={count} className={classes.listItem}
                                                        onMouseEnter={handlePopoverOpen}
                                                        onMouseLeave={handlePopoverClose}
                                                    >
                                                        <GamepadIcon style={{ fontSize: 30, color: '#1976d2', padding: 6 }} />
                                                        <ListItemText primary={`${element.room}`} style={{ color: '#1976d2' }} />
                                                    </ListItem>
                                                    <Divider />
                                                </div>
                                            );
                                        })
                                    }
                                </List>
                            </Popover>
                        }
                    </div>
                );
            })}
        </List >
    );
}
