import * as React from 'react'
import {
    InputBase,
    IconButton,
    Button,
    Grid,
    List,
    ListItem,
    ListItemIcon,
    ListItemText,
    Popover,
    Typography,
    Hidden,
} from '@material-ui/core';
import Router from 'next/router';
import SearchIcon from '@material-ui/icons/Search';
import ArrowDropUp from '@material-ui/icons/ArrowDropUp';
import ArrowDropDown from '@material-ui/icons/ArrowDropDown';
import { makeStyles } from '@material-ui/core/styles';
import EcoIcon from "@material-ui/icons/Eco";
import ViewList from "@material-ui/icons/ViewList";
import Moment from 'react-moment';
import GamepadIcon from '@material-ui/icons/Gamepad';

const useStyles = makeStyles((theme) => ({
    inputbase: {
        width: '600px',
        height: 50,
        float: 'left',
        marginLeft: 12,
        // alignItems: 'center',
        // flex: 1,
    },
    searchHome: {
        width: '100%',
        minHeight: '500px',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center center',
        opacity: '1',
        // position: 'relative',
        backgroundSize: 'cover',
        backgroundImage: 'url("https://i2.wp.com/dogoquoccuong.com/wp-content/uploads/2014/08/DSC09545.jpg?fit=3008%2C2000&ssl=1")'
    },
    searchHomeBody: {
        position: 'absolute',
        top: 100,
        backgroundColor: 'white',
        maxWidth: '100%',
    },
    viewSearch: {
        display: 'inline-block',
        backgroundColor: '#a3d2fa',
        height: 50,
        color: 'white',
        width: 70,
    },
    iconSearch: {
        fontSize: 25,
    },
    iconButton: {
        padding: '10px 20px',
        marginTop: 4,
        fontSize: 25,
    },
    listItem: {
        backgroundColor: '#e3f2fd',
        "&:hover": {
            // backgroundColor: '#90caf9',
        }
    },
    textItem: {
        color: '#1976d2',
    },
    popover: {
        pointerEvents: 'none', // none
    },
}));

const SearchHomepage: React.FunctionComponent = () => {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const onChangeWards = (event: any) => {
        if (event.key == "Enter" || event.keyCode == 13) {
            // onClickSearch();
        }
    };

    const imageBackground = [
        'https://i2.wp.com/dogoquoccuong.com/wp-content/uploads/2014/08/DSC09545.jpg?fit=3008%2C2000&ssl=1',
        'https://www.noithathoanmy.com.vn/blog/wp-content/uploads/2018/11/noi-that-go-phong-khach-dep.jpg',
        'https://vatlieuxaydung.org.vn/Upload/48/Nam_2018/Thang_12/Ngay_19/vlxd.org_nguyentac2.jpg',
        'https://www.noithathoanmy.com.vn/blog/wp-content/uploads/2018/11/noi-that-go-phong-khach-dep.jpg',
    ];

    const suggestions = [
        { title: 'Giường ngủ hiện đại', link: '/bad' },
        { title: 'Giường ngủ cổ điển', link: '/bad' },
        { title: 'Bàn ăn 4 ghế', link: '/bad' },
        { title: 'Bàn ăn 6 ghế', link: '/bad' },
        { title: 'Bàn ăn hiện đại', link: '/bad' },
    ]

    const history = [
        { title: 'Giường ngủ', link: '/history' },
        { title: 'bàn ăn', link: '/history' },
        { title: 'tủ quần áo', link: '/history' },
        { title: 'tủ giày', link: '/history' },
        { title: 'tủ ghế nhà hàng', link: '/history' },
    ]

    const renderSuggestion = () => {
        return (
            <React.Fragment style={{ weight: 500, pointerEvents: 'auto' }}>
                {
                    suggestions.map((item, count) => {
                        return (
                            <ListItem button key={count}>
                                {item.title}
                                {/* <ListItemText primary={`${item.title}`} style={{ color: '#1976d2' }} /> */}
                            </ListItem >
                        );
                    })
                }
            </React.Fragment>
        );
    }

    const renderHistrory = () => {
        return (
            <React.Fragment>

            </React.Fragment>
        );
    }

    const handlePopoverOpen = (event, index) => {
        setAnchorEl(event.currentTarget);
        setOpen(true);
    };

    return (
        <Grid container spacing={0} item xs={12} alignItems="center" justify='center'
            className={classes.searchHome}>
            <Grid item xs className={classes.searchHomeBody}>
                <InputBase
                    className={classes.inputbase}
                    placeholder={'Tìm sản phẩm mong muốn ...'}
                    inputProps={{ 'aria-label': 'Tìm sản phẩm mong muốn ...' }}
                    onKeyUp={e => onChangeWards(e)}
                    onClick={handlePopoverOpen}
                />
                <div className={classes.viewSearch}>
                    <IconButton type="submit" className={classes.iconButton} aria-label="search">
                        <SearchIcon className={classes.iconSearch} />
                    </IconButton>
                </div>
                <Popover
                    className={classes.popover}
                    open={open}
                    anchorEl={anchorEl}
                    anchorOrigin={{ vertical: 'right', horizontal: 'right' }} // Ghim
                    transformOrigin={{ vertical: 'top', horizontal: 'left' }} // Vi tri hien thi
                // onMouseLeave={handlePopoverClose}
                >
                    {renderSuggestion()}
                </Popover>
            </Grid>
        </Grid>
    );
};

export default SearchHomepage;
