import React from 'react';
import Popover from '@material-ui/core/Popover';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    popover: {
        pointerEvents: 'none',
    },
    paper: {
        padding: theme.spacing(1),
    },
}));

export default function MouseOverPopover() {
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handlePopoverOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handlePopoverClose = () => {
        setAnchorEl(null);
    };

    const open = Boolean(anchorEl);
    const Product = [
        {
            name: 'Giường ngủ',
            elements: [{ element: 'Giường gỗ tự nhiên', element: 'Giường gỗ nhân tạo', element: 'Giường gỗ cổ điển', element: 'Giườn hiện đại' }]
        },
        {
            name: 'Bàn ăn',
            elements: [{ element: 'Bàn ăn tự nhiên', element: 'Bàn ăn nhân tạo', element: 'Bàn ăn cổ điển', element: 'Bàn ăn hiện đại' }]
        },
    ]
    return Product.map((item, index) => {
        return (
            <div ket={index}>
                <Typography
                    aria-owns={open ? 'mouse-over-popover' : undefined}
                    aria-haspopup="true"
                    color='primary'
                    onMouseEnter={handlePopoverOpen}
                    onMouseLeave={handlePopoverClose}
                >
                    {item.name}
                </Typography>
                <Popover
                    id="mouse-over-popover"
                    className={classes.popover}
                    classes={{
                        paper: classes.paper,
                    }}
                    open={open}
                    anchorEl={anchorEl}
                    anchorOrigin={{ // Vị trí ghim
                        vertical: 'center', // Theo chieu doc
                        horizontal: 'center', //  theo chieu ngang
                    }}
                    transformOrigin={{ // Biến đổi
                        vertical: 'center',
                        horizontal: 'left',
                    }}
                    onClose={handlePopoverClose}
                    disableRestoreFocus
                >
                    {
                        item.elements.map((e, count) => {
                            return <Typography key={count}>{e.element}</Typography>
                        })
                    }
                </Popover>
            </div>
        );
    })

}
