import React from 'react'
import PropTypes from 'prop-types'
import withStyles from '@material-ui/core/styles/withStyles'
import {
   CardContent,
   Typography,
   CardActionArea,
   CardMedia,
   Card,
   Avatar,
   Hidden,
   Grid,
} from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'
import MoreVertIcon from '@material-ui/icons/MoreVert'
import Skeleton from '@material-ui/lab/Skeleton'
import StarRateIcon from '@material-ui/icons/StarRate'
import what2 from './Image/what2.jpg'
import what3 from './Image/what3.jpg'
import what4 from './Image/what4.jpg'
import moment from 'moment'
import _ from 'lodash'

const styles = theme => ({
   viewPhone: {
      [theme.breakpoints.down('xs')]: {
         padding: '8px'
      },
   },
   card: {
      maxWidth: 200,
      border: '3px solid white',
      [theme.breakpoints.down('sm')]: {
         // position: 'fixed',
         maxWidth: 170,
      }
   },
   title: {
      color: 'white',
      marginTop: '30px',
      marginBottom: '15px'
   },
   cssIcon: {
      color: 'white',
      fontSize: '30px'
   },
   imgZoom: {
      transition: "transform .5s, filter 3s ease-in-out",
      filter: "grayscale(100%)",
   },
   imgZoom: {
      "&:hover": {
         filter: "grayscale(0)",
         transform: "scale(1.1)",
         transitionDuration: "1s",
         transitionTimingFunction: "linear",
      }
   }
})

const arrImg = [
   {
      image: "https://thebank.vn/static/6/1135/714/90/2018/10/16/thebank_suthatvephiquetthetindungkhithanhtoan1min_1539654231.jpg",
      title: 'Mua hàng tiện lợi',
   },
   {
      image: what4, //"https://www.thietkekientrucgroup.com/public/upload/images/noi-that-go-phong-khach%20(76).jpg",
      title: 'Bảo hành tận nhà',
   },
   {
      image: what2, //"https://www.noithathoanmy.com.vn/blog/wp-content/uploads/2018/11/noi-that-go-phong-khach-dep.jpg",
      title: 'Đạt chuẩn xuất khẩu',
   },
   {
      image: what3, //"https://i2.wp.com/dogoquoccuong.com/wp-content/uploads/2014/08/DSC09545.jpg?fit=3008%2C2000&ssl=1",
      title: 'Chất lượng hoàng gia',
   },
]
class Index extends React.Component {
   constructor(props) {
      super(props);
   }

   renderStartIcon() {
      let { classes } = this.props
      const array = [{ item: 1 }, { item: 1 }, { item: 1 }, { item: 1 }, { item: 1 }, { item: 1 }];
      return array.map((item, index) => <StarRateIcon key={index} className={classes.cssIcon} />)
   }

   render() {
      let { classes } = this.props
      let img = "url('http://noithatdangcap.vn/app/webroot/uploads/files/1-phong-khach-noi-that-co-dien-chau-au-dinh-thu-tai-Nam-Dinh.jpg')"
      // let img = "url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTuUdQSvSrGwfA1ATgX4LgizzClGH-SO-jj4k_vMZOk2hgq5Jzf&s')"
      // let img = "url('https://png.pngtree.com/58pic/32/49/67/20U58PICI9dWcd7X7yX8e_PIC2018.jpg')"
      return (
         <div style={{ backgroundImage: img, backgroundSize: 'cover', height: 350, width: '100%', }} >
            <div style={{ color: '#2196f3', textAlign: 'center' }} >
               <Grid container spacing={0}>
                  <Grid item xs={12}>
                     <Typography className={classes.title} variant='h4' component='h4'>
                        HÃY ĐẾN VỚI CHÚNG TÔI
                     </Typography>
                     {this.renderStartIcon()}
                  </Grid>
               </Grid>
               <br></br>
               <center>
                  <Grid container spacing={0} >
                     {
                        arrImg.map((element, index) => {
                           let link = '/convenient'
                           switch (index) {
                              case 1:
                                 link = './export'
                                 break
                              case 2:
                                 link = './quality'
                                 break
                              case 3:
                                 link = './guarantee'
                           }
                           return (
                              <Grid item xs={6} sm={3} md={3} key={index} className={classes.viewPhone}>
                                 <Card className={classes.card}>
                                    <CardActionArea onClick={() => this.goto(link)}>
                                       <CardMedia
                                          className={classes.imgZoom}
                                          component="img"
                                          alt="Contemplative Reptile"
                                          height="100"
                                          image={element.image}
                                          title={element.title}
                                       />
                                       <CardContent style={{ backgroundColor: '#1976d2' }} >
                                          <Typography variant="body2" style={{ color: 'white' }} >
                                             {element.title}
                                          </Typography>
                                       </CardContent>
                                    </CardActionArea>
                                 </Card>
                              </Grid>
                           )
                        })
                     }
                  </Grid>
               </center>
            </div>
         </div>
      )
   }
}

Index.propTypes = {
   classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Index)
