import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import InfoIcon from '@material-ui/icons/Info';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import { I18n } from 'react-redux-i18n';
import {
    IconButton,
    Icon,
    Tooltip,
    Button,
    Card,
    Grid,
    CardContent,
    CardActions,
    Typography,
    AppBar,
    Toolbar,
    Hidden,
    Popover,

} from '@material-ui/core';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { titles } from '../../constants/index';
import moment from 'moment'
import _ from 'lodash'

const useStyles = makeStyles((theme) => ({
    rootList: {
        width: '100%',
        height: 20,
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
    },
    title: {
        display: 'inline',
        fontWeight: 500,
        color: '#1976d2',
        fontSize: 16,
        padding: '0 8px'
    },
    popover: {
        pointerEvents: 'none',
    },
    paper: {
        padding: theme.spacing(1),
    },
}));


const Actions = (props) => {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [indexOpen, setIndex] = React.useState(null);

    const popoverOpen = (event, index) => {
        setOpen(event.currentTarget);
        setIndex(index);
    };

    const popoverClose = () => {
        setOpen(null);
        setIndex(null);
    };

    return (
        <Grid container spacing={0} style={{ height: 40, width: '100%' }}>
            <Grid item xs={12} style={{ padding: '8px 80px' }}>
                {titles.map((item, index) => {
                    return (
                        <span>
                            <Typography
                                key={index}
                                className={classes.title}
                                onMouseEnter={(event) => popoverOpen(event, index)}
                                onMouseLeave={popoverClose}
                            >
                                {item.title}
                            </Typography>
                            {indexOpen === index && <Popover
                                id={"mouse-over-popover" + index}
                                className={classes.popover}
                                classes={{
                                    paper: classes.paper,
                                }}
                                open={open}
                                anchorEl={open}
                                anchorOrigin={{ // Vị trí ghim
                                    vertical: 'bottom', // Theo chieu doc
                                    horizontal: 'left', //  theo chieu ngang
                                }}
                                transformOrigin={{ // Biến đổi
                                    vertical: 'top',
                                    horizontal: 'bottom',
                                }}
                                onClose={popoverClose}
                                disableRestoreFocus
                            >
                                <div>
                                    {item.elements.map((element, count) => {
                                        return (
                                            <List component="nav" className={classes.root} aria-label="mailbox folders">
                                                <ListItem>
                                                    <ListItemText primary={element.children} />
                                                </ListItem>
                                                <Divider />
                                            </List>
                                        )
                                    }
                                    )}
                                </div>
                            </Popover>}
                        </span>
                    )
                })}
            </Grid>
        </Grid>
    )
};

export default Actions;
