import gql from "graphql-tag"

const GET_INVESTOR_LIST = gql`
  query investors ($page: Int!, $limit: Int!, $filter: String!) {
    investors (page: $page, filter: $filter, limit: $limit) {
      edges {
        node {
          id
          name
        }
      }
    }
  }
`;

const GET_INVESTOR_BY_ID = gql`
  query investor ($slug: String!) {
    investor (id: $slug) {
      id
      name
      description
    }
  }
`;

export {
  GET_INVESTOR_BY_ID,
  GET_INVESTOR_LIST
}
