import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField, Button, Icon, IconButton} from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import Box from '@material-ui/core/Box';
import SendIcon from '@material-ui/icons/Send';
import {GET_NAME_AD_SELL_LEASES_BY_ID} from "../../../graphql/ad_sell_leases/queries"
import {apolloClient} from "../../../utils"
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
// moment.locale('vi')

const useStyles = makeStyles(theme => ({
  root: {
  },
  iconUp: {
    fontSize: "10",
  },
  subheader: {
    flexWrap: 'wrap',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

interface ListCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}


interface OverallReviewsProps {
  className?:string,
  listComment: ListCommentProps[]
} 

interface Review {
  id: string,
  reviewer: {
    avatar: string,
    name: string,
  },
  rating: number,
  created_at: string,
  message: string
};


let reviews = [
  {
    id: "141545616541654",
    reviewer: {
      avatar: "https://zicxa.com/hinh-anh/wp-content/uploads/2019/07/T%E1%BB%95ng-h%E1%BB%A3p-h%C3%ACnh-%E1%BA%A3nh-g%C3%A1i-xinh-d%E1%BB%85-th%C6%B0%C6%A1ng-cute-nh%E1%BA%A5t-6.jpg",
      name: "Nguyễn Hồng Thúy"
    },
    rating: 3,
    created_at: "1 3 2020",
    message: "Hùng thật sự rất tuyệt vời mỗi khi gặp khách hàng",
  }, 
  {
    id: "215474854156478",
    reviewer: {
      avatar: "https://thuthuatnhanh.com/wp-content/uploads/2019/07/anh-girl-xinh-facebook-tuyet-dep-387x580.jpg",
      name: "Mai Trâm"
    },
    rating: 4,
    created_at: "1 5 2019",
    message: "Tư vấn rất chuyên nghiệp nhiệt tình, khá thân thiện với khách hàng. Tôi đánh giá rất cao chuyên môn của bạn",
  }
]

const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  const [value, setValue] = React.useState<number | null>(3);
  const [rating, setRating] = React.useState<number | null>(0)
  const [message, setMessage] = React.useState<string>("")
  const [reviewLocal, setReviewLocal] = React.useState<Review[]>([])
  const fullReviews = reviews.concat(reviewLocal)
  let couter: number = 0

  React.useEffect(() => {
   
  })

  fullReviews.map((item: any) => {
    couter = item.rating + couter
  })
  const setReview = (e: any, name: string) => {
    let item = {
      id: `${new Date().getTime()}`,
      reviewer: {
        avatar: "https://caygiongnongnghiep.com/wp-content/uploads/2017/08/cay-hoa-hong-doi-mau.jpg",
        name: "Minh Quang"
      },
      rating: rating,
      created_at: moment().format("DD MM YYYY"),
      message: message,
    }
    if(name === "buttonSen"){
      addReview(item) 
      setMessage("")
      setRating(0)
    }
    if (name === "textField" && e.key == "Enter" || e.keyCode == 13) {
      addReview(item) 
      setMessage("")
      setRating(0)
    }
  }
  const addReview = (item: any) => setReviewLocal(state => [...reviewLocal, item])
  let ratingGeneral = couter/fullReviews.length
  const { className, ...rest } = props;
  const classes = useStyles();
  console.log("props.infoUser", props.listComment)
  return (
    <div  className={clsx(classes.root, className)}>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Card className={clsx(classes.root, className)}>
            <CardContent>
              <Grid alignItems="center" container spacing={3}>
                <Grid item>
                  <Typography color="textSecondary" variant="body2" >
                    {props.listComment.length} lượt bình luận
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      {props.listComment.map((item: any, index: number) => {
        let adSellLeasesTitle = apolloClient.query({query: GET_NAME_AD_SELL_LEASES_BY_ID, variables: {
          "slug": item.node.ad_sell_lease_id
          }}).then(response => {
            return response;
          });
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={item.node.user.avatar} >
                      {item.node.user.first_name}  {item.node.user.last_name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      <Typography variant="body2">
                        Bình luận bởi{' '} <b>{item.node.user.first_name} {item.node.user.last_name} </b> 
                      </Typography>
                      <Typography variant="body2">
                        {moment(Number(item.node.created_at)).fromNow()}
                      </Typography>
                      <Typography variant="body2">
                        Dự án : 
                      </Typography>
                    </div>
                  }
                />
                <div className={classes.message}>
                  <Typography variant="subtitle2">{item.node.content}</Typography>
                </div>
              </Card>
            </Grid>
          </Grid>
        )  
      })}
    </div>
  );
};


export default Project;
