import React from 'react'
import { makeStyles } from '@material-ui/core/styles'
import GridList from '@material-ui/core/GridList'
import GridListTile from '@material-ui/core/GridListTile'
import GridListTileBar from '@material-ui/core/GridListTileBar'
import ListSubheader from '@material-ui/core/ListSubheader'
import InfoIcon from '@material-ui/icons/Info'
import PropTypes from 'prop-types'
import withStyles from '@material-ui/core/styles/withStyles'
import { I18n } from 'react-redux-i18n'
import {
    IconButton,
    Icon,
    Tooltip,
    Button,
    Card,
    Grid,
    CardContent,
    CardActions,
    Typography,
    AppBar,
    Toolbar,
    Hidden,
    Popover,

} from '@material-ui/core'
import StarBorderIcon from '@material-ui/icons/StarBorder'
import { titles } from '../../constants/index';
import moment from 'moment'
import _ from 'lodash'

const useStyles = makeStyles((theme) => ({
    popover: {
        pointerEvents: 'none',
    },
    paper: {
        padding: theme.spacing(1),
    },
}));


const Actions = (props) => {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const popoverOpen = (event) => {
        setOpen(event.currentTarget);
    };

    const popoverClose = () => {
        setOpen(null);
    };

    return (
        <Grid container spacing={0} style={{ height: 40, width: '100%' }}>
            <Grid item xs={12} style={{ padding: '8px 80px' }}>
                {titles.map((item, index) => {
                    return (
                        <span>
                            <Typography key={index} style={{ display: 'inline', fontWeight: 500, color: '#1976d2', fontSize: 16, padding: '0 8px' }}>
                                {item.title}
                            </Typography>
                            <Popover
                                id={"mouse-over-popover" + index}
                                className={classes.popover}
                                classes={{
                                    paper: classes.paper,
                                }}
                                open={open}
                                anchorEl={open}
                                anchorOrigin={{ // Vị trí ghim
                                    vertical: 'center', // Theo chieu doc
                                    horizontal: 'center', //  theo chieu ngang
                                }}
                                transformOrigin={{ // Biến đổi
                                    vertical: 'top',
                                    horizontal: 'left',
                                }}
                                onClose={popoverClose}
                                disableRestoreFocus
                            >
                                <div>
                                    {item.elements.map((e, count) => <Typography key={count}>{e.children}</Typography>)}
                                </div>
                            </Popover>
                        </span>
                    )
                })}
            </Grid>
        </Grid>
    )
};

export default Actions;
