import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {
    List,
    Divider,
    ListItemIcon,
    ListItem,
    ListItemText,
    Collapse,
    ListSubheader,
    Popover,
    Typography,

} from '@material-ui/core';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import GamepadIcon from '@material-ui/icons/Gamepad';
import { listMenu } from '../../constants/index';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
    },
    nested: {
        paddingLeft: theme.spacing(4),
    },
    popover: {
        pointerEvents: 'none',
        width: '100%',
    },
    paper: {
        padding: theme.spacing(1),
    },
}));

export default function NestedList() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = () => {
        setOpen(!open);
    };

    const handlePopoverOpen = (event) => {
        setAnchorEl(event.currentTarget);
        setOpen(true);
        console.log('toang')
    };

    const handlePopoverClose = () => {
        setAnchorEl(null);
        console.log('toang')
    };
    return (
        <List
            component="nav"
            aria-labelledby="nested-list-subheader"
            className={classes.root}
            style={{ backgroundColor: '#e3f2fd' }}
        >
            {listMenu.map((item, index) => {
                return (
                    <>
                        <ListItem button onMouseEnter={handlePopoverOpen} onMouseLeave={handlePopoverClose}>
                            <GamepadIcon style={{ fontSize: 30, color: '#1976d2', padding: 6 }} />
                            <Typography aria-owns={open ? 'mouse-over-popover' : undefined} aria-haspopup="true">
                                <ListItemText primary={`${item.title}`} style={{ color: '#1976d2' }} />
                            </Typography>
                        </ListItem>
                        {index + 1 !== listMenu.length && <Divider />}
                        <Popover
                            id="mouse-over-popover"
                            className={classes.popover}
                            classes={{ paper: classes.paper }}
                            open={open}
                            anchorEl={anchorEl}
                            anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
                            transformOrigin={{ vertical: 'top', horizontal: 'left' }}
                            onClose={handlePopoverClose}
                            disableRestoreFocus
                        >
                            <div style={{ width: '100%', height: 400, }}>
                                <Typography>I use Popover.</Typography>
                            </div>
                        </Popover>
                    </>
                );
            })}
        </List >
    );
}
