import React, { FunctionComponent } from "react";
import _ from 'lodash'
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import Banner from "./Banner";
import Introduce from "./Introduce";
import Benefit from "./Benefit"
import AboutUs from "./AboutUs";
import { introduceInfo, infoTitle, priceListTitle, priceListContent } from "./Constants"
import { makeStyles } from "@material-ui/core/styles"

const indexStyles = makeStyles((theme) => ({
  root: {
    padding: "40px 80px", 
    color: '#444',
    [theme.breakpoints.down("sm")]: {
      padding: "8px 8px", 
    },
  },
  paddingIntroduceInfo: {
    padding: "12px", 
    textAlign: "justify"
  },
  infoTitle: {
    padding: "20px 200px", 
    fontWeight: 900,
    fontSize: '30px',
    textAlign: "center",
    [theme.breakpoints.down("sm")]: {
      padding: "8px 16px", 
      fontWeight: 700,
    },
  },
  priceTitle: {
    fontSize: "30px",
    fontWeight: 900, 
    textAlign: "center",
    [theme.breakpoints.down("sm")]: {
      fontSize: "20px",
      fontWeight: 700, 
      textAlign: "center",
    },
  },
  priceContent: { 
    textAlign: "center", 
    padding: "16px 8px", 
  },
  priceDetail: { 
    textAlign: "center", 
    padding: "12px", 
    color: "#1A4684", 
    cursor: 'default', 
    fontWeight: 700 
  }
}));

const Index: FunctionComponent = () => {
  const classes = indexStyles();
  return (
    <div>
      <Banner/>
      <div className={classes.root} >
        <Grid container spacing={2}>
          <Grid item xs={2}> </Grid>
          <Grid item xs={8}>
            <div className={classes.paddingIntroduceInfo} >
              {introduceInfo}
            </div>
            <div className={classes.infoTitle}>
              {infoTitle}
            </div>
          </Grid>
          <Grid item xs={2}></Grid>
        </Grid>
        
       {/*  <Introduce/>
        <Benefit />
        <Typography className={classes.priceTitle}>
          {priceListTitle}
        </Typography>
        <Typography className={classes.priceContent}>
          {priceListContent}
        </Typography>
        <Typography className={classes.priceDetail}>
          Hiển thị chi tiết
        </Typography> */}
        {/* <AboutUs/> */}
      </div>
    </div>
  );
};

export default Index;
