import React, {useEffect} from "react";
import Layout from '../../components/Layout/Layout';
//Import SCSS
import "../../assets/scss/style.scss";

import BannerSlider from "../../components/PageDetail/BannerSlider";
import Main from "../../components/AdSellLeases/AdSellLeasesMain";
import BottomFix from "../../components/PageDetail/BottomFix";
import {NextPage} from "next";
import {apolloClient} from "../../utils";
import {GET_AD_SELL_LEASES_BY_SLUG} from "../../graphql/ad_sell_leases/queries";
import {COUNT_VIEW_AD_SELL_LEASE} from "../../graphql/ad_sell_leases/mutation";
import StringProcess from "./../../modules/stringProcess";
import {localStorageCompareKey, mainURL} from "../../constants";
import urlProcess from "../../modules/urlProcess";
import Validator from "../../modules/validator";
import PriceProcess from "../../modules/priceProcess";

interface AdSellLeaseItem {
  id: string;
  title: string;
  description: string;
  slug: string;
  price: {
    unit: string
    value: string
  },
  avatar: {
    alt_text: string
    src: string
    description: string
  },
  address: {
    text: string,
    ward: string,
    district: string,
    city: string
  },
  area: {
    unit: string
    value: string
  },
  image: ImageProp[];
  type: {
    type1: string,
    type2: string,
    text: string
  },
  status: string
  project: string
  seo_title: string
  seo_description: string
  floor_number: number
  living_room_number: number
  bed_room_number: number
  toilet_room_number: number
  gara_number: number
  material: {
    id: string
    text: string
  },
  is_basement: number
  is_car_into_home: number
  is_corner: number
  is_elevator: number
  furniture: number
  length: string
  width: string
  year_built: string
  map: {
    lat: string
    lng: string
  },
  updated_at: string
  created_at: string
  view_count: number
  contact: {
    image: string
    name: string
    address: string
    mobile: string
    phone: string
    email: string
  }
}

interface ImageProp {
  alt_text: string
  description: string
  title: string
  src: string
}

interface AdSellLeaseDetailProp {
  url: {
    query: {
      slug: string
    }
  },
  adSellLease: {
    data: {
      adSellLease: AdSellLeaseItem
    }
  },
  slug: string
}
interface QueryProp {
  query: {
    slug: string
  }
}

interface CompareDataProps {
  avatar: string,
  title: string|null,
  id: string,
  type: string
}

// @ts-ignore
const AdSellLeaseDetailPage: NextPage<AdSellLeaseDetailProp> = (props: AdSellLeaseDetailProp) => {
  let title = (props.adSellLease.data.adSellLease.title)?props.adSellLease.data.adSellLease.title:'Xem chi tiết tin rao';
  let albumImage = props.adSellLease.data.adSellLease.image;
  let priceUnit = props.adSellLease.data.adSellLease.price.unit;
  let priceValue = props.adSellLease.data.adSellLease.price.value;

  let sizeArea = (
    <>
      {props.adSellLease.data.adSellLease.width} x {props.adSellLease.data.adSellLease.length} m<sup>2</sup>
    </>
  );

  let square = (<>m<sup>2</sup></>);
  let areaString = (
    <>
      {StringProcess.addCommas(props.adSellLease.data.adSellLease.area.value)} {(props.adSellLease.data.adSellLease.area.unit == "m2")?square:props.adSellLease.data.adSellLease.area.unit}
    </>
  );

  const freshText = (input: string) => {
    input = StringProcess.stripProperties(input, []);
    input = StringProcess.stripTags(input, '');
    input = input.replace(/&nbsp;/g, ' ');
    return input;
  };

  const convertDescription = (input: string) => {
    return input.replace(/&#(\d+);/g, function(match, dec) {
      return String.fromCharCode(dec);
    });
  };

  let metaTitle = '';
  let metaDescription = '';
  if (props.adSellLease.data.adSellLease.seo_title) {
    metaTitle = props.adSellLease.data.adSellLease.seo_title;
  }
  if (props.adSellLease.data.adSellLease.seo_description) {
    metaDescription = props.adSellLease.data.adSellLease.seo_description;
  }
  if (metaTitle === '' || !metaTitle) {
    metaTitle = title;
  }

  if (metaDescription === '' || !metaDescription) {
    metaDescription = freshText(props.adSellLease.data.adSellLease.description);
    metaDescription = convertDescription(metaDescription);
    metaDescription = StringProcess.truncateString(metaDescription, 255, '');
  }

  let imageAvatar = 'https://static.nhadatmoi.net/default/home_default.jpg';
  if (props.adSellLease.data.adSellLease.avatar && props.adSellLease.data.adSellLease.avatar.src) {
    imageAvatar = urlProcess.processImageUrl(props.adSellLease.data.adSellLease.avatar.src, 'ad_sell_lease');
  }

  //Count Ad Sell Lease
  useEffect(() => {
    let adSellLeaseId = props.adSellLease.data.adSellLease.id;
    apolloClient.mutate({mutation: COUNT_VIEW_AD_SELL_LEASE, variables: {
        "input": {
          "id": adSellLeaseId
        }
      }}).then((response: any) => {
    });
  }, []);

  const [dataCompare, setDataCompare] = React.useState(null);
  let updateCompareData = (data: CompareDataProps) => {
    let dataToCompare = [];
    let isAddToLocal = true;
    if (localStorage.getItem(localStorageCompareKey) && !Validator.isBlank(localStorage.getItem(localStorageCompareKey))) {
      dataToCompare = JSON.parse(localStorage.getItem(localStorageCompareKey) as string);
      for (let index in dataToCompare) {
        if (dataToCompare[index].id === data.id) {
          isAddToLocal = false;
        }
        if (dataToCompare[index].type !== data.type) {
          localStorage.removeItem(localStorageCompareKey);
          dataToCompare = [];
          break;
        }
      }
    }
    if (isAddToLocal) {
      dataToCompare.push(data);
      // @ts-ignore
      setDataCompare(dataToCompare);
      localStorage.setItem(localStorageCompareKey, JSON.stringify(dataToCompare));
    }
  };

  let priceToReturn = PriceProcess.priceProcess(priceValue, priceUnit);

  let propertyOverview = {
    id: props.adSellLease.data.adSellLease.id,
    building_density: '',
    type: (props.adSellLease.data.adSellLease.type.text)?props.adSellLease.data.adSellLease.type.text:'-',
    status: (props.adSellLease.data.adSellLease.status)?props.adSellLease.data.adSellLease.status:'-',
    floor_number: props.adSellLease.data.adSellLease.floor_number,
    living_room_number: props.adSellLease.data.adSellLease.living_room_number,
    bed_room_number: props.adSellLease.data.adSellLease.bed_room_number,
    toilet_room_number: props.adSellLease.data.adSellLease.toilet_room_number,
    gara_number: props.adSellLease.data.adSellLease.gara_number,
    material: (props.adSellLease.data.adSellLease.material.text)?props.adSellLease.data.adSellLease.material.text:'-',
    length: props.adSellLease.data.adSellLease.length,
    width: props.adSellLease.data.adSellLease.width,
    price: priceToReturn,
    project: String(props.adSellLease.data.adSellLease.project),
    area: areaString,
    year_built: props.adSellLease.data.adSellLease.year_built.toString(),
    address: props.adSellLease.data.adSellLease.address,
    size: sizeArea,
    description: props.adSellLease.data.adSellLease.description,
    map: props.adSellLease.data.adSellLease.map,
    title: props.adSellLease.data.adSellLease.title,
    updated_at: props.adSellLease.data.adSellLease.updated_at,
    created_at: props.adSellLease.data.adSellLease.created_at,
    position_content: '',
    view_count: props.adSellLease.data.adSellLease.view_count,
    infrastructure_content: ''
  };
  return (
    <Layout
      title={metaTitle}
      description={metaDescription}
      {...props}
      image={imageAvatar}
      canonical={ mainURL + '/tin-rao/' + props.slug}
      path={'tin-rao'}
      dataCompare={dataCompare}
    >
      <BannerSlider images={albumImage} imageType='ad_sell_lease'/>
      <Main
        propertyOverview={propertyOverview}
        images={albumImage}
        contact={props.adSellLease.data.adSellLease.contact}
        objectUrl={ mainURL + '/tin-rao/' + props.slug}
        imageType='ad_sell_lease'
        avatar={props.adSellLease.data.adSellLease.avatar.src}
        updateCompareData={(data: CompareDataProps) => updateCompareData(data)}
      />
      <BottomFix contact={props.adSellLease.data.adSellLease.contact} objectUrl={ mainURL + '/tin-rao/' + props.slug}/>
    </Layout>
  );
};

// @ts-ignore
AdSellLeaseDetailPage.getInitialProps = async function(props: QueryProp) {
  let projectSlug = props.query.slug;
  let adSellLeases = await apolloClient.query({query: GET_AD_SELL_LEASES_BY_SLUG, variables: {
      "slug": projectSlug
    }}).then(response => {
    return response;
  });
  return {
    adSellLease: adSellLeases,
    slug: projectSlug
  };
};


export default AdSellLeaseDetailPage;
