import React from 'react';
import clsx from 'clsx';
import { 
    Grid, Typography, Button, Icon, 
    IconButton, List, ListItem, ListItemText,  Divider
} from "@material-ui/core"
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles'
import ListItemIcon  from '@material-ui/core/ListItemIcon';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import WcIcon from '@material-ui/icons/Wc';
import PhoneIcon from '@material-ui/icons/Phone';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import EmailIcon from '@material-ui/icons/Email';


const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        color: "#aaa",
        backgroundColor: theme.palette.background.paper,
    },

    posts: {
        marginTop: theme.spacing(2)
    },
    post: {
        marginBottom: theme.spacing(2)
    }
}));


interface InfoProps {
    className?: string
    infoUser: User
}

interface User {
    address: string
    avatar: string
    birthday: string
    email: string
    facebook: string
    first_name: string
    gender: string
    google_id: string
    id: string
    intro: string
    last_name: string
    phone: string
    mobile: string
    skype: string
    username: string
    viber: string
    website: string
    zalo: string
    created_at: string
    updated_at: string
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
    const { className } = props
    const classes = useStyles()
    let dataInfo = `<p>Được th&agrave;nh lập từ nh&agrave; m&aacute;y Coterraz, trung t&acirc;m kinh doanh BĐS v&agrave; hai x&iacute; nghiệp x&acirc;y lắp, C&ocirc;ng ty COTECLAND l&agrave; một trong những đơn vị trực thuộc COTEC GROUP v&agrave; được cổ phần ho&aacute; theo Quyết định số 2013/QĐ-BXD ng&agrave;y 17/12/2004 của bộ trưởng Bộ X&acirc;y dựng.</p>
    <p>Sau khi c&oacute; quyết định cổ phần h&oacute;a của bộ trưởng Bộ X&acirc;y dựng, Sở kế hoạch v&agrave; đầu tư Tp.HCM đ&atilde; cấp giấy chứng nhận đăng k&yacute; kinh doanh số 4103003080 ng&agrave;y 28/01/2005.</p>
    <p>Lĩnh vực hoạt động</p>
    <p>- Tư vấn đầu tư, quản l&yacute; dự &aacute;n c&aacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp, c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng khu đ&ocirc; thị v&agrave; c&ocirc;ng nghiệp.</p>
    <p>- X&acirc;y dựng, lắp đặt, sửa chữa c&aacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng, c&ocirc;ng nghiệp, c&ocirc;ng tr&igrave;nh kỹ thuật hạ tầng khu đ&ocirc; thị v&agrave; khu c&ocirc;ng nghiệp, c&aacute;c c&ocirc;ng tr&igrave;nh giao th&ocirc;ng đường bộ, đường thuỷ.</p>
    <p>- Thiết kế kiến tr&uacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp</p>
    <p>- Khai th&aacute;c, sản xuất, mua b&aacute;n vật liệu x&acirc;y dựng</p>
    <p>C&aacute;c lĩnh vực hoạt động ch&iacute;nh hiện nay</p>
    <p>- Dịch vụ quản l&yacute; sau dự &aacute;n.</p>
    <p>- Đầu tư v&agrave; kinh doanh bất động sản</p>
    <p>- Đầu tư t&agrave;i ch&iacute;nh</p>
    <p>- X&acirc;y lắp</p>
    <p>
    <p>Được th&agrave;nh lập từ nh&agrave; m&aacute;y Coterraz, trung t&acirc;m kinh doanh BĐS v&agrave; hai x&iacute; nghiệp x&acirc;y lắp, C&ocirc;ng ty COTECLAND l&agrave; một trong những đơn vị trực thuộc COTEC GROUP v&agrave; được cổ phần ho&aacute; theo Quyết định số 2013/QĐ-BXD ng&agrave;y 17/12/2004 của bộ trưởng Bộ X&acirc;y dựng.</p>
    <p>Sau khi c&oacute; quyết định cổ phần h&oacute;a của bộ trưởng Bộ X&acirc;y dựng, Sở kế hoạch v&agrave; đầu tư Tp.HCM đ&atilde; cấp giấy chứng nhận đăng k&yacute; kinh doanh số 4103003080 ng&agrave;y 28/01/2005.</p>
    <p>Lĩnh vực hoạt động</p>
    <p>- Tư vấn đầu tư, quản l&yacute; dự &aacute;n c&aacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp, c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng khu đ&ocirc; thị v&agrave; c&ocirc;ng nghiệp.</p>
    <p>- X&acirc;y dựng, lắp đặt, sửa chữa c&aacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng, c&ocirc;ng nghiệp, c&ocirc;ng tr&igrave;nh kỹ thuật hạ tầng khu đ&ocirc; thị v&agrave; khu c&ocirc;ng nghiệp, c&aacute;c c&ocirc;ng tr&igrave;nh giao th&ocirc;ng đường bộ, đường thuỷ.</p>
    <p>- Thiết kế kiến tr&uacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng v&agrave; c&ocirc;ng nghiệp</p>
    <p>- Khai th&aacute;c, sản xuất, mua b&aacute;n vật liệu x&acirc;y dựng</p>
    <p>C&aacute;c lĩnh vực hoạt động ch&iacute;nh hiện nay</p>
    <p>- Dịch vụ quản l&yacute; sau dự &aacute;n.</p>
    <p>- Đầu tư v&agrave; kinh doanh bất động sản</p>
    <p>- Đầu tư t&agrave;i ch&iacute;nh</p>
    <p>- X&acirc;y lắp</p>`

    return (
        <div className={clsx(classes.root, className)}>
            <Grid container spacing={2}>
                <Grid item xs={12} >
                    <Typography>Giới thiệu</Typography>
                    <div 
                        className="project-info__description u-margin-bottom-medium" 
                        dangerouslySetInnerHTML={{ __html: dataInfo }} 
                    />
                </Grid>
           </Grid>
           <Grid container spacing={2}>
                <Grid item xs={12} >

                </Grid>
            </Grid>
        </div>
    );
};


export default Info;
