import gql from "graphql-tag";
const GET_AD_SELL_LEASE_COMMENTS = gql`
query adSellLeaseComments($page: Int!, $limit: Int!, $filter: String!) {
  adSellLeaseComments(page: $page, limit: $limit, filter: $filter) {
    edges {
      node {
        id
        ad_sell_lease_id
        content
        user_id
        created_at
        ad_sell_lease_id
        media
        user {
          first_name
          last_name
          avatar
        }
        reply
        like_number
        trash
        spam
        created_at
      }
    }
  }
}
`;

export {
  GET_AD_SELL_LEASE_COMMENTS,
}
