import React from 'react';
import { Grid, Typography, Divider, Hidden } from "@material-ui/core";
import { introStyles } from "../Constant";
import { GET_CHANNEL_BY_SLUG } from "../../../graphql/channel/queries";
import { apolloClient } from "../../../utils";
import _ from "lodash";

interface InfoProps {
  channelId: string
  slug: string
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
  const classes = introStyles();
  const [description, setDescription] = React.useState();
  const getChannel = async () => {
    let res = await apolloClient.query({ query: GET_CHANNEL_BY_SLUG, variables: {
        "slug": props.slug,
      }}).then((response: any) => {
      let description =_.get(response, "data.channel.description", "")
      setDescription(description);
    });
  };

  React.useEffect(() => {
    getChannel();
  });

  const renderInfo = () => {
    return (
      <div>
        <Hidden mdUp>
          <Grid container spacing={2} className={classes.paddingInfo}>
            <Grid item xs={12} sm={6} >
              <Typography variant="h6" className={classes.titleContent}>Thông tin</Typography>
              <Typography className={classes.content}> Quốc gia: </Typography>
              <Typography variant="body2" className={classes.contentInfo}>Việt nam</Typography><br/>
              <Typography className={classes.content}> Ngôn ngữ: </Typography>
              <Typography variant="body2" className={classes.contentInfo}>Tiếng Việt</Typography>
            </Grid>
            <Grid item xs={12} sm={6} >
              <Typography variant="h6" className={classes.titleContent}>Chi tiết công ty</Typography>
              <Typography className={classes.content}>Dịch vụ:</Typography>
              <Typography variant="body2" className={classes.contentInfo}>Cung cấp nền tảng công nghệ giao lưu bất động sản</Typography><br/>
              <Typography className={classes.content}>Lĩnh vực kinh doanh:</Typography>
              <Typography variant="body2" className={classes.contentInfo}>Trao đổi thông tin bất động sản</Typography>
            </Grid>
          </Grid>
          <Grid container spacing={2} className={classes.paddingInfo}>
            <Grid item xs={12} sm={6} >
              <Typography variant="h6" className={classes.titleContent}>Liên hệ</Typography>
              <Typography className={classes.content}> Số điện thoại:</Typography>
              <Typography variant="body2" className={classes.contentInfo}> 0987.654.321 </Typography><br/>
              <Typography className={classes.content}> Địa chỉ:</Typography>
              <Typography variant="body2" className={classes.contentInfo}> Số 64 - Tố Hữu - Hà Đông - Hà Nội </Typography>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Typography variant="h6" className={classes.titleContent}>Các liên kết</Typography>
              <Typography className={classes.content}>Facebook: </Typography>
              <Typography variant="body2" className={classes.contentInfo}>
                <a href="https://nhadatmoi.net">https://nhadatmoi.net</a></Typography><br/>
              <Typography className={classes.content}>Website:</Typography>
              <Typography variant="body2" className={classes.contentInfo}>
                <a href="https://nhadatmoi.net">https://nhadatmoi.net</a></Typography>
            </Grid>
          </Grid>
        </Hidden>
        <Hidden smDown>
          <Grid container spacing={2} className={classes.paddingInfo}>
            <Grid item xs={12} sm={6} >
              <Typography variant="h6" className={classes.titleContent}>Thông tin</Typography>
              <Typography className={classes.content}> Quốc gia: </Typography>
              <Typography variant="body2" className={classes.contentInfo}>Việt nam</Typography><br/>
              <Typography className={classes.content}> Ngôn ngữ: </Typography>
              <Typography variant="body2" className={classes.contentInfo}>Tiếng Việt</Typography>
            </Grid>
            <Grid item xs={12} sm={6} >
              <Typography variant="h6" className={classes.titleContent}>Chi tiết công ty</Typography>
              <Typography className={classes.content}>Dịch vụ:</Typography>
              {/* <Typography variant="body2" className={classes.contentInfo}>Cung cấp nền tảng công nghệ giao lưu bất động sản</Typography><br/> */}
              <Typography className={classes.content}>Lĩnh vực kinh doanh:</Typography>
              <Typography variant="body2" className={classes.contentInfo}>Trao đổi thông tin bất động sản</Typography>
            </Grid>
          </Grid>
          <Divider className={classes.divider}/>
          <Grid container spacing={2} className={classes.paddingInfo}>
            <Grid item xs={12} sm={6} >
              <Typography variant="h6" className={classes.titleContent}>Liên hệ</Typography>
              <Typography className={classes.content}> Số điện thoại:</Typography>
              <Typography variant="body2" className={classes.contentInfo}> 0987.654.321 </Typography><br/>
              <Typography className={classes.content}> Địa chỉ:</Typography>
              <Typography variant="body2" className={classes.contentInfo}> Số 64 - Tố Hữu - Hà Đông - Hà Nội </Typography>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Typography variant="h6" className={classes.titleContent}>Các liên kết</Typography>
              <Typography className={classes.content}>Facebook: </Typography>
              <Typography variant="body2" className={classes.contentInfo}>
                <a href="https://nhadatmoi.net">https://nhadatmoi.net</a></Typography><br/>
              <Typography className={classes.content}>Website:</Typography>
              <Typography variant="body2" className={classes.contentInfo}>
                <a href="https://nhadatmoi.net">https://nhadatmoi.net</a></Typography>
            </Grid>
          </Grid>
        </Hidden>
      </div>
    );
  };

  return (
    <div className={classes.rootIntro}>
      <Typography variant="h5" className={classes.titleIntro}>Giới thiệu</Typography>
      {description ?
      <div 
        className="project-info__description u-margin-bottom-medium" 
        dangerouslySetInnerHTML={{ __html: description }} 
      />
      : <Typography align="center">Thông tin giới thiệu đang cập nhật</Typography>  }
      {renderInfo()}
    </div>
  );
};


export default Info;
