import React, { FunctionComponent, useEffect} from 'react';
import {makeStyles} from '@material-ui/core/styles'
import Info from './Tabs/Info'
import Reviews from "./Tabs/Reviews"
import Ratings from "./Tabs/Ratings"
import AdSellLeases from "./Tabs/AdSellLeases"
import Header from "./Header"
import Paper from '@material-ui/core/Paper';
import {Tabs, Tab, Divider, colors} from '@material-ui/core';
import {apolloClient} from "../../utils";
import {GET_USER_BY_ID } from "../../graphql/user/queries/index"
import {GET_AD_SELL_LEASES} from "../../graphql/ad_sell_leases/queries"
import Page from "./Page";
 
const useStyles = makeStyles(theme => ({
  root: {},
  rootTabs: {
    flexGrow: 1,
  },
  inner: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3)
  },
  divider: {
    backgroundColor: colors.grey[300]
  },
  content: {
    marginTop: theme.spacing(3)
  }
}));

interface IndexProps {
  comments: CommentsProp[],
  infoUser: UserProps,
  ratings: RatingsProp[],
}

interface CommentsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface UserProps {
  address: string
  avatar: string
  birthday: string
  email: string
  facebook: string
  first_name: string
  gender: string
  google_id: string
  intro: string
  id: string
  last_name: string
  phone: string
  mobile: string
  skype: string
  username: string
  viber: string
  website: string
  zalo: string
  created_at: string
  updated_at: string
}

interface RatingsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    rate: number,
    like_number: string,
    created_at: string,
  },
}

const Index: FunctionComponent<IndexProps> = (props: IndexProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = React.useState("ad")
  const tabs = [
    {value: 'info', label: 'Thông tin'},
    {value: 'ad', label: 'Tin rao'},
    {value: 'reviews', label: "Bình luận"},
    {value: "ratings", label: "Đánh giá"},
  ]

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value)
  }

  return (
    <Page title="Profile">
      <Header infoUser={props.infoUser}/>
      <div className={classes.inner}>
        <Paper square className={classes.rootTabs}>
        <Tabs
          onChange={handleTabsChange}
          scrollButtons="auto"
          indicatorColor="primary"
          textColor="primary"
          value={itemTab}
        >
          {tabs.map(tab => (
            <Tab key={tab.value} label={tab.label} value={tab.value} />
          ))}
        </Tabs>
        </Paper>
        <Divider className={classes.divider}/>
        <div className={classes.content}>
          {itemTab === 'info' && <Info infoUser={props.infoUser}/>}
          {itemTab === 'ad' && <AdSellLeases userId={props.infoUser.id}/>}
          {itemTab === 'reviews' && <Reviews comments={props.comments} userId={props.infoUser.id}/>}
          {itemTab === 'ratings' && <Ratings comments={props.comments} userId={props.infoUser.id}/>}
        </div>
      </div>
    </Page>
  )
}

export default Index;
