import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Skeleton from '@material-ui/lab/Skeleton';
 
const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
    margin: theme.spacing(2),
  },
  media: {
    height: 190,
  },
}))

interface MediaProps {
  loading?: boolean;
  listad: AdProps[],
}
 
interface AdProps  {
  node: {
    name: string,
    id: string,
    title: string,
    seo_title: string,
    created_at: string,
    updated_at: string,
    slug: string,
    project: string,
    length:  string,
    width: string,
    floor_number: string,
    living_room_number: 0
  },
}


const ListMedia: React.FunctionComponent<MediaProps> = (props: MediaProps) => {
  const { loading = false } = props;
  const classes = useStyles();
  console.log("|props", props)
  return ( 
    <Grid container spacing={4}>
      {props.listad.map((item: any, index: number) => {
        return ( 
          <Grid item xs={3}>
            <Card className={classes.card} key={index}>
              <CardHeader
                avatar={<Avatar alt="XXX" src="https://vcdn-giaitri.vnecdn.net/2020/01/23/C-OiQ-UUIAIycqR-1-1579796735_680x0.jpg" />}
                // action={<IconButton aria-label="settings"> <MoreVertIcon /> </IconButton>}
                title={item.node.title}
                subheader={item.node.created_at}
              />
              <CardMedia 
                className={classes.media} 
                title="Ted talk"
                image={item.node.avatar.src}
              />
              <CardContent>
                <Typography variant="body2" color="textSecondary" component="p">
                  {item.node.seo_title}
                </Typography>
              </CardContent>
            </Card>
          </Grid>   
        )
      })}
    </Grid>   
  )
}

export default ListMedia;
