import React from "react";
import { Avatar, Typography, Button, Grid } from "@material-ui/core";
import StyledCheckbox from './StyledCheckbox'
import { headerStyles } from './Constant'
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import { FOLLOW_CHANNEL, UNFOLLOW_CHANNEL } from "../../graphql/channel/mutation"
import { GET_CHANNEL_BY_SLUG } from "../../graphql/channel/queries" 
import { apolloClient } from "../../utils";
import _ from "lodash"

interface HearderProps {
  userLogin: string
  slug: string
  channel: ChannelProp
}

interface ChannelProp {
  id: string
  name: string
  slug: string
  avatar: string
  cover: string
  description: string
  nation: string
  language: string
  telephone: string
  address: string
  facebook: string
  youtube: string
  website: string
  official_status: number
  follow_value: number
  followers: {
    user_id: string
  }[]
  created_at: string
}

const Header: React.FunctionComponent<HearderProps> = (props: HearderProps) => {
  const classes = headerStyles();
  const avatar = "https://sf.co.ua/14/07/wallpaper-117233.jpg" // "https://news.mogi.vn/wp-content/uploads/2018/08/anh-nha-dep-13.jpg"

  const [followValue, setFollowValue] = React.useState(follow_value)

  const follow = async ()=> { 
    let follow = await apolloClient.mutate({ mutation: FOLLOW_CHANNEL, variables: {
      "input": {
        "id": props.channel.id
      }
    }})
    .then((response: any) => {
      setFollowValue(followValue + 1)
    })
  };

  const unfollow = async ()=> { 
    let follow = await apolloClient.mutate({ mutation: UNFOLLOW_CHANNEL, variables: {
      "input": {
        "id": props.channel.id
      }
    }})
    .then((response: any) => {
      setFollowValue(followValue - 1)
    })
  };
  const follow_value = _.get(props, "channel.follow_value", "")
  console.log("followValue >>. ", props?.channel?.follow_value, "\nfollowValue", followValue)
  return (
    <div>
      <div className={classes.cover}></div>
      <div className={classes.container}>
        <Avatar
          alt="nhadatmoi.net"
          className={classes.avatar}
          src={avatar}
        />   
        <Grid container spacing={0}>
          <Grid item xs={8} sm={6}>
            <div className={classes.titleFollow}>
              <Typography component="h2" gutterBottom className={classes.title}> 
                Nhà Đất Mới <CheckCircleIcon style={{ color: "#045EA8", fontSize: "15px" }} /> 
              </Typography>
              <Typography component="h3" variant="body2" className={classes.follow}>
                {followValue > 0 ? followValue : 0} lượt theo dõi
              </Typography>
            </div>
          </Grid>
          <Grid item xs={4} sm={6}>
            <div className={classes.followStyle}>
              <Button color="primary" onClick={() => follow()} fullWidth variant="outlined">Theo dõi</Button>
            </div>
            <div className={classes.followStyle}>
              <Button color="primary" onClick={() => unfollow()} fullWidth variant="contained">Bỏ theo dõi</Button>
            </div>
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

export default Header;
