import gql from "graphql-tag";

const SEN_MESSAGE = gql`
  mutation update($input: ChatMessageInput!) {
    chatMessage (input: $input) {
      id
      conversation {
        message {
          text
          attachment {
            type
            payload {
              url
            }
          }
        }
      }
    }
  }
`;


const UPDATE_MESSAGE = gql`
  mutation update($input: ChatMessageInput!) {
    chatMessage (input: $input) {
      id
      conversation {
        id
        message {
          text
          attachment {
            type
            payload {
              url
            }
          }
        }
        status_check
        read_at
        send_at
      }
      create_at
      update_at
    }
  }
`

const _UPDATE_MESSAGE = gql`
  mutation update($input: ChatMessageInput!) {
    chatMessage (input: $input) {
      id
      conversation {
        id
        send_user_id
        message {
          text
          attachment {
            type
            payload {
              url
            }
          }
        }
        status_check
        read_at
        send_at
      }
      create_at
      update_at
    }
  }
`
export {
  SEN_MESSAGE,
  UPDATE_MESSAGE
}
