import React from "react";
import { Avatar, Typography, Button, Grid } from "@material-ui/core";
import { headerStyles } from './Constant';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import { FOLLOW_CHANNEL, UN_FOLLOWED_CHANNEL } from "../../graphql/channel/mutation";
import { apolloClient } from "../../utils";
import _ from "lodash";

interface HeaderProps {
  userLogin: string
  slug: string
  channel: ChannelProp
};

interface ChannelProp {
  id: string
  name: string
  slug: string
  avatar: string
  cover: string
  description: string
  nation: string
  language: string
  telephone: string
  address: string
  facebook: string
  youtube: string
  website: string
  official_status: number
  follow_value: number
  followers: {
    user_id: string
  }[]
  created_at: string
};

const Header: React.FunctionComponent<HeaderProps> = (props: HeaderProps) => {
  const classes = headerStyles();
  const avatar = "https://sf.co.ua/14/07/wallpaper-117233.jpg"; // "https://news.mogi.vn/wp-content/uploads/2018/08/anh-nha-dep-13.jpg"
  let follow_value = _.get(props, "channel.follow_value", "");
  let [followValue, setFollowValue] = React.useState(0);
  const [checkFollow, setCheckFollow] = React.useState(false);

  const addFollow = async ()=> { 
    setFollowValue(follow_value + 1 )
    let follow = await apolloClient.mutate({ mutation: FOLLOW_CHANNEL, variables: {
      "input": {
        "id": _.get(props, "channel.id", "")
      }
    }})
    .then((response: any) => {
      setCheckFollow(true)
    });
  };

  const unFollowed = async ()=> { 
    setFollowValue(follow_value - 1)
    let follow = await apolloClient.mutate({ mutation: UN_FOLLOWED_CHANNEL, variables: {
      "input": {
        "id": _.get(props, "channel.id", "")
      }
    }})
    .then((response: any) => {
      setCheckFollow(false)
    });
  };

  let follow = followValue || follow_value;
  let followers = _.get(props, "channel.followers", "");
  let check = false;
  if(Array.isArray(followers)) {
    followers.map((item: any) => {
      if(props.userLogin == item.user_id) {
        check = true
      }
    })
  };

  return (
    <div>
      <div className={classes.cover}></div>
      <div className={classes.container}>
            <div style={{ backgroundColor: "gray", width: "100%"}}>
              <div className={classes.channelAvatar}>
                <Avatar src={avatar} className={classes.sideAvatar} alt="ndm"/> 
              </div>
              <div 
                style={{
                  width: "calc(100% - 260px)",
                  height: "100px",
                  float: "left",
                  backgroundColor: "orange",
                }}
              >
                {/* {_.get(props, "channel.name", "")}  */}
                {/* <Typography className={classes.contentTitle} > 
                  { _.get(props, "channel.official_status", "") ? <CheckCircleIcon className={classes.checkOfficial}/> : ""} 
                </Typography> */}
                {/* <Typography component="h3" variant="body2" className={classes.follow}>
                  {follow > 0 ? follow : 0} lượt theo dõi 
                </Typography> */}
              </div>
            </div>
            {/* <div className={classes.followStyle} style={{ float: "float", width: "calc(100%-200px)", height: "20px"  }}>
              {checkFollow || check
                ? <button  onClick={() => unFollowed()} style={{ width: "100%", height: '40px', border: "2px solid #045EA8", borderRadius: "4px"}}>Bỏ theo dõi</button>
                : <button onClick={() => addFollow()} style={{ width: "100%", height: '40px', border: "2px solid #045EA8",  borderRadius: "4px" }} >Theo dõi</button>  
              }
            </div> */}
            <div style={{ clear: "both" }}></div>
      </div>
    </div>
  );
};

export default Header;
