import React from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField, Button, Icon, IconButton} from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import {apolloClient} from "../../../utils"
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import {GET_AD_SELL_LEASE_COMMENTS, GET_AD_SELL_LEASE_RATINGS} from "../../../graphql/reviews/queries";
import {GET_USER_BY_ID } from "../../../graphql/user/queries/index"
import Box from '@material-ui/core/Box';
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

const useStyles = makeStyles(theme => ({
  root: {
  },
  iconUp: {
    fontSize: "10",
  },
  subheader: {
    flexWrap: 'wrap',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

const avatar = "https://1.bp.blogspot.com/-gx54Ig64I_k/WnktM_nzPXI/AAAAAAAAHgw/E7pSRwrXu_c3tSSg-vbO2iQgg4GbVxGKwCLcBGAs/s1600/anh-girl-xinh-9x-kute%2B%252811%2529.jpg"
const Ratings: React.FunctionComponent<any> = () => {
  const classes = useStyles();
  const ratings: any = [
    {
      name: "Nguyễn Thùy Dung",
      rating: 4,
      avatar: avatar,
      time: 1581540507166
    },
    {
      name: "Nguyễn Thùy Dung",
      rating: 4,
      avatar: avatar,
      time: 1580240507166
    },
  ]

  return (
    <div  className={(classes.root)}>
      {ratings.length 
      ? ratings.map((item: any, index: number) => {
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={avatar} >
                      {item.name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      <Typography variant="body2">
                        Đáng giá bởi{' '} <b>{item.name}</b> 
                      </Typography>
                      <Rating
                        name="simple-controlled"
                        value={item.rating}
                      />
                      <Typography variant="body2">
                        {moment(Number(new Date().getTime())).fromNow()}
                      </Typography>
                    </div>
                  }
                />
              </Card>
            </Grid>
          </Grid>
        )  
      }) 
      : ""
    }
    {/* <Grid container spacing={4}>
        <Grid item>
        { props.ratings.length >= 15 ? <Button onClick={setViewPage} >Hiển thị thêm</Button> : "" }
        </Grid>
    </Grid> */}
    </div>
  );
};


export default Ratings;
