import React from "react";
import { Grid, Typography } from "@material-ui/core";
import { Avatar } from "@material-ui/core";
import PhoneInTalkIcon from '@material-ui/icons/PhoneInTalk';
import { memberStyles} from "../Constant";
import { GET_MEMBER_CHANNEL_BY_ID } from "../../../graphql/channel/queries";
import { apolloClient } from "../../../utils";
import LoadingCircle from "../LoadingCircle";

interface InfoProps {
  channelId: string
};

interface Member {
  node: {
    avatar: string
    first_name: string
    last_name: string
    mobile: string
    phone: string
  }
};

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
  const classes = memberStyles();
  const [members, getMembers] = React.useState<Member[]>([]);
  const [loading, setLoading] = React.useState(true);

  const getMemberChannel = async ()=> { 
    let id = props.channelId;
    let members = await apolloClient.query({ query: GET_MEMBER_CHANNEL_BY_ID, variables: {
      "id": id,
      "limit": 18,
      "page": 1,
    }})
    .then((response: any) => {
      return response.data.channelMembers.edges
    });
    if(Array.isArray(members)){
      getMembers(members)
      setLoading(false)
    }
  };

  React.useEffect(() => {
    getMemberChannel();
  });

  const renderMembers = () => {
    return (
      <Grid container direction="row" alignItems="center" spacing={1}>
        {Array.isArray(members) ? members.map((item: any, index: number) => {
          let phone = item.node.mobile || item.node.phone
          return (
            <Grid key={index} item lg={2} md={3} sm={4} xs={6} className={classes.marginMember}>
              <div className={classes.members} >
                <Avatar alt="" className={classes.avatar} src={item.node.avatar} />
                <div className={classes.nameMember}>
                  <Typography>{item.node.first_name} {item.node.last_name}</Typography>
                </div>
                <a title="Gọi ngay" href={`${phone}`}>
                  <PhoneInTalkIcon />
                </a>
                <Typography>{phone ? phone : "SĐT: Đang cập nhật ... " }</Typography>
              </div>
            </Grid>
          )
        }) : <Typography align="center">Không có môi giới nào</Typography> }
      </Grid>
    );
  };

  return (
    <div className={classes.rootMember}>
      <Typography variant="h5" className={classes.titleMember}>Danh sách môi giới</Typography>
      {loading ? <LoadingCircle /> : ""} 
      {renderMembers()}
    </div>
  );
};

export default Info;
