import React from 'react';
import clsx from 'clsx';
import { Grid } from "@material-ui/core"
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import WcIcon from '@material-ui/icons/Wc';
import PhoneIcon from '@material-ui/icons/Phone';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import EmailIcon from '@material-ui/icons/Email';
// import uuid from "uuid";
// import dynamic from 'next/dynamic'
// const uuid=dynamic(import ('uuid'),{ssr:false})


const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        backgroundColor: theme.palette.background.paper,
      },

    posts: {
        marginTop: theme.spacing(2)
    },
    post: {
        marginBottom: theme.spacing(2)
    }
}));


interface InfoProps {
    className?: string
    user: User
}

interface User {
    address: String
    avatar: String
    birthday: String
    email: String
    facebook: String
    first_name: String
    gender: String
    google_id: String
    id: String
    intro: String
    last_name: String
    phone: String
    mobile: String
    skype: String
    username: String
    viber: String
    website: String
    zalo: String
    created_at: String
    updated_at: String
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
    const { className } = props
    const classes = useStyles()
    let userName = `${props.user.first_name} ${props.user.last_name}`
    return (
        // <div className={clsx(classes.root, className)}>
            <Grid container spacing={2}>
                <Grid item xs={8} lg={8}>
                    <List component="nav" className={classes.root} dense={true} aria-label="mailbox folders">
                        <ListItem button divider={true}>
                            <ListItemIcon><AccountCircleIcon color="primary" /></ListItemIcon>
                            <ListItemText primary={userName} />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><WcIcon  color="primary"/></ListItemIcon>
                            <ListItemText primary={props.user.gender ? props.user.gender : "Đang cập nhật"} />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><PhoneIcon color="primary" /></ListItemIcon>
                            <ListItemText primary={props.user.mobile ? props.user.mobile : "Đang cập nhật"}  />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><LocationOnIcon  color="primary"/></ListItemIcon>
                            <ListItemText primary={props.user.address ? props.user.address : "Đang cập nhật"} />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><EmailIcon color="primary" /></ListItemIcon>
                            <ListItemText primary={props.user.email ? props.user.email : "Đang cập nhật"} />
                        </ListItem> <Divider/>
                    </List>
                </Grid>
               <Grid item xs={4}>

               </Grid>
           </Grid>
        // </div>
    );
};


export default Info;
