import React, { useEffect, useState } from 'react';
import { Button, Typography, Grid, Icon } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import Validator from "../../modules/validator";
import SearchIcon from '@material-ui/icons/Search';
import { localDataSearch } from "../../constants";
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
    paper: {
      marginRight: theme.spacing(2),
    },
  }),
);

const dataProjectKey = [
  {
    title: "Vinhomes Central Park",
  },
  {
    title: "Vinhomes Grand Park",
  },
  {
    title: "Vinhomes Smart City",
  },
  {
    title: "Nhà Đất Hải Phòng",
  },
  {
    title: "Nhà Đất Hà Nội",
  }
]

let dataSearch = new Array()
export default function MenuListComposition() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [valueSearch, setValueSearch] = React.useState("");
  const anchorRef = React.useRef(null);

  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  }
  const handleClose = (event: any, index: number) => {
    if(index !== -1){
      let titleSearch = dataProjectKey[index].title || ""
      setValueSearch(titleSearch)
    }
    setOpen(false);
  }
  function handleListKeyDown(event: any) {
    if (event.key === 'Tab') {
      event.preventDefault();
      setOpen(false);
    }
  }
  const prevOpen = React.useRef(open)
  useEffect(() => {
    dataSearch = JSON.parse(localStorage.getItem(localDataSearch) as string)
    if(!Validator.isBlank(localStorage.getItem(localDataSearch))) {
      localStorage.setItem(localDataSearch, JSON.stringify(dataSearch))
    } 
    if (prevOpen.current === true && open === false) {
      // anchorRef.current.focus();
    }
    prevOpen.current = open
  }, [open])

  

  const onChangeSearch = (e: any) => {
    setValueSearch(e.target.value)
  }

  const onBlur = (e: any) => {
    if(e.target.value){
      dataSearch.unshift({ title: e.target.value})
      if(dataSearch.length == 4){
        dataSearch.splice(3, 1)        
      }
      localStorage.setItem(localDataSearch, JSON.stringify(dataSearch))
    }
    console.log("KKKKK", dataSearch)
  }
  
  const handleCloseSearch = (event: any, index: number) => {
    if(index !== -1){
      let titleSearch = dataProjectKey[index].title || ""
      setValueSearch(titleSearch)
    }
    // if (anchorRef.current && anchorRef.current.contains(event.target)) {
    //   return;
    // }
    setOpen(false);
  }
  const viewSearch = dataProjectKey.map((item: any, index: number) => {
    return (
      <MenuItem key={index} onClick={(event: any) => handleCloseSearch(event, index)}>{item.title}</MenuItem>
    )
  })

  const handleCloseHistory = (event: any, index: number) => {
    if(index !== -1){
      let titleSearch = dataSearch[index].title || ""
      setValueSearch(titleSearch)
    }
    setOpen(false)
  }

  const viewHistorySearch = dataSearch.map((item: any, index: number) => {
    return (
      <MenuItem key={index} onClick={(event: any) => handleCloseHistory(event, index)}>{item.title}</MenuItem>
    )
  })

  return (
    <div className={classes.root}>
      <Grid container spacing={1} justify="flex-start" alignItems="center">
        <Grid item xs={4}>
          <TextField
            fullWidth={true}
            id="outlined-basic" 
            variant="outlined"
            onChange={e => onChangeSearch(e)}
            onBlur={e => onBlur(e)}
            value={valueSearch}
            autoComplete="off"
            ref={anchorRef}
            margin="dense"
            onClick={handleToggle}
          />
        </Grid>
        <Grid item xs={6}>
          <Button><SearchIcon />Tìm kiếm</Button>
        </Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={12}>
          <Popper open={open} anchorEl={anchorRef.current} role={undefined} transition disablePortal>
              {({ TransitionProps, placement }) => (
                <Grow {...TransitionProps}
                  style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }}
                >
                  <Paper>
                    <ClickAwayListener onClickAway={(event: any) => handleClose(event, -1)}>
                      <MenuList autoFocusItem={open} id="menu-list-grow" onKeyDown={handleListKeyDown}>
                        <MenuItem>
                          <b>
                            <Typography style={{ fontSize: "Roboto, sans-serif" }}>
                              TỪ KHÓA HOT
                            </Typography>
                          </b>
                        </MenuItem>
                        {viewSearch}
                        <MenuItem>
                          <b>
                            <Typography style={{ fontSize: "Roboto, sans-serif", marginRight: "120px" }} >
                              LỊCH SỬ TÌM KIẾM
                            </Typography>
                          </b>
                          <Typography style={{ color: "red" }}>Xóa </Typography>
                        </MenuItem>
                        {viewHistorySearch}
                      </MenuList>
                    </ClickAwayListener>
                  </Paper>
                </Grow>
              )}
            </Popper>
        </Grid>
      </Grid>
    </div>
  );
}
