import React, { FunctionComponent, useState, useEffect} from 'react';
// import {Redirect, BrowserRouter as Router, useHistory, useParams} from "react-router-dom"
import {makeStyles} from '@material-ui/core/styles'
import {GET_USER_ID_BY_SLUG_AD} from "../../graphql/ad_sell_leases/queries"
import Info from './Tabs/Info'
import Projects from "./Tabs/Projects"
import Reviews from "./Tabs/Reviews"
import Ad from "./Tabs/Ad"
import Header from "./Header"
import Paper from '@material-ui/core/Paper';
import {Tabs, Tab, Divider, colors} from '@material-ui/core';
import {apolloClient} from "../../utils";
import {GET_USER_BY_ID } from "../../graphql/user/queries/index"
import {GET_AD_SELL_LEASES} from "../../graphql/ad_sell_leases/queries"
import Page from "./Page";
 
const useStyles = makeStyles(theme => ({
  root: {},
  rootTabs: {
    flexGrow: 1,
  },
  inner: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3)
  },
  divider: {
    backgroundColor: colors.grey[300]
  },
  content: {
    marginTop: theme.spacing(3)
  }
}));

interface ProfileProps {
  listComment: ListCommentProps[]
  userId: string
  infoUser: UserProps
}

interface ListCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface AdProps  {
  node: {
      title: string,
      slug: string,
      created_at: string,
      updated_at: string,
      price: {
        unit: string
        value: string
      },
      avatar: {
        alt_text: string
        src: string
        description: string
      },
      address: {
        text: string,
        ward: string,
        district: string,
        city: string
      },
      contact: {
        image: string,
        name: string,
        address: string,
        mobile: string,
        phone: string,
        email: string,
      }
      area: {
        unit: string
        value: string
      }
  },
}

interface UserProps {
  address: String
  avatar: String
  birthday: String
  email: String
  facebook: String
  first_name: String
  gender: String
  google_id: String
  intro: String
  id: String
  last_name: String
  phone: String
  mobile: String
  skype: String
  username: String
  viber: String
  website: String
  zalo: String
  created_at: String
  updated_at: String
}

interface QueryProp { 
  query: { slug: string } 
}

const Index: FunctionComponent<ProfileProps> = (props: ProfileProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = React.useState("reviews")
  const [list_ad, setListAd] = React.useState<AdProps[]>([]);
  const [user , setUser] = React.useState();
  const tabs = [
      {value: 'info', label: 'Thông tin'},
      {value: 'projects', label: 'Dự án'},
      {value: 'ad', label: 'Tin rao'},
      {value: 'reviews', label: "Đánh giá"}
  ]

  useEffect(() => {
    let userId = "5e7b82a48928eb4d96c1a526" // userTest
    if(props.userId) {
      let dataFilterAd = { 
        user: props.userId,
        // user: userId
      }
      let filterUser = JSON.stringify(dataFilterAd)
      apolloClient.query({query: GET_AD_SELL_LEASES, variables: {
        "page": 1,
        "limit": 50,
        "filter": filterUser 
      }}).then(response => { 
      setListAd(response.data.adSellLeases.edges)
      })
      apolloClient.query({query: GET_USER_BY_ID, variables: {
        "id": userId
      }}).then(response => { 
        setUser(response.data.user)
      })
    }
  }, [])

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value)
  }
  console.log("props.infoUser", props.infoUser)

  return (
    <Page title="Profile">
      <Header user={props.infoUser}/>
      <div className={classes.inner}>
        <Paper square className={classes.rootTabs}>
        <Tabs
          onChange={handleTabsChange}
          scrollButtons="auto"
          indicatorColor="primary"
          textColor="primary"
          value={itemTab}
        >
          {tabs.map(tab => (
            <Tab key={tab.value} label={tab.label} value={tab.value} />
          ))}
        </Tabs>
        </Paper>
        <Divider className={classes.divider}/>
        <div className={classes.content}>
          {itemTab === 'info' && <Info user={props.infoUser} />}
          {itemTab === 'projects' && <Projects />}
          {itemTab === 'ad' && <Ad list_ad={list_ad}/>}
          {itemTab === 'reviews' && <Reviews listComment={props.listComment} />}
        </div>
      </div>
    </Page>
  )
}

export default Index;
