import React from 'react';
import { Typography, CardHeader, Avatar, Grid } from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import { ratings, ratingStyles } from "../Constant"
import { apolloClient } from "../../../utils";
import { GET_RATINGS_CHANNEL_BY_ID, GET_AVERAGE_RATING } from "../../../graphql/channel/queries";
import GradeIcon from '@material-ui/icons/Grade';
import _ from "lodash"
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

interface Props {
  channelId: string
}

interface RatingProps {
  node: {
    id: string
    user_id: {
      id: string
      first_name: string
      last_name: string
      avatar: string
    }
    ad_sell_lease_id: {
      id: string
      title: string
    }
    rate: Number
    created_at: string
  }
}

interface AverageRating {
  count_rating: number
  average_point: number
  five_star: number
  four_star: number
  three_star: number
  two_star: number
  one_star: number
}

const avatar = "https://1.bp.blogspot.com/-gx54Ig64I_k/WnktM_nzPXI/AAAAAAAAHgw/E7pSRwrXu_c3tSSg-vbO2iQgg4GbVxGKwCLcBGAs/s1600/anh-girl-xinh-9x-kute%2B%252811%2529.jpg"
const Ratings: React.FunctionComponent<Props> = (props: Props) => {
  const classes = ratingStyles();
  const [averageRating, setAverageRating] = React.useState<AverageRating>()
  const [ratingsChannel, setRatingsChannel] = React.useState<RatingProps[]>([])
  
  React.useEffect(() => {
    getAdChannel()
    getAverageRating()
  })

  const getAdChannel = async () => { 
    let id = props.channelId
    let ratingsChannel = await apolloClient.query({ query: GET_RATINGS_CHANNEL_BY_ID, variables: {
      "id": id,
      "limit": 18,
      "page": 1,
    }})
    .then((response: any) => {
      return response.data.channelAdSellLeaseRatings.edges
    })
    if(Array.isArray(ratingsChannel)){
      setRatingsChannel(ratingsChannel)
    }
  };

  const getAverageRating = async () => { 
    let res = await apolloClient.query({ query: GET_AVERAGE_RATING, variables: {
      "id": props.channelId
    }})
    .then((response: any) => {
      let averageRating = _.get(response, "data.channelAverageRatings", {})
      setAverageRating(averageRating)
    })
  };

  const renderRating = () => {
    return ratings.map((item: any, index: number) => {
      return (
        <div key={index}>
          <CardHeader 
            avatar={ <Avatar alt="Reviewer" src={avatar}>{item.name}</Avatar>}
            disableTypography
            subheader={
              <div className={classes.subheader}>
                <Typography variant="body2" className={classes.rating}>Đánh giá bởi:</Typography>
                <Typography className={classes.userRating}>{item.name}</Typography><br />
                <Rating
                  disabled
                  name="simple-controlled"
                  value={item.rating}
                  style={{ color: "#045EA8" }}
                />
                <Typography variant="body2">{moment(item.time).fromNow()}</Typography>
              </div>
            }
          />
        </div>
      )  
    }) 
  }

  const renderRatingChanel = () => {
    return ratingsChannel.map((item: any, index: number) => {
      return (
        <div key={index}>
          <CardHeader 
            avatar={ <Avatar alt="Reviewer" src={item.node.user_id.avatar || avatar}>{item.node.user_id.avatar}</Avatar>}
            disableTypography
            subheader={
              <div className={classes.subheader}>
                <Typography variant="body2" className={classes.rating}>Đánh giá bởi:</Typography>
                <Typography className={classes.userRating}>{item.node.user_id.first_name} {item.node.user_id.last_name}</Typography><br />
                <Rating
                  disabled
                  name="simple-controlled"
                  value={item.node.rate}
                  style={{ color: "#045EA8" }}
                />
                <Typography variant="body2">{moment(Number(item.node.created_at)).fromNow()}</Typography>
              </div>
            }
          />
        </div>
      )  
    }) 
  }

  const average_point  = parseInt(`${_.get(averageRating, "average_point", 0)}`)
  const count_rating   = _.get(averageRating, "count_rating", 1)
  const fiveRating     = parseInt(`${(_.get(averageRating, "five_star", 0)/count_rating)*100}`)
  const fourRating     = parseInt(`${(_.get(averageRating, "four_star", 0)/count_rating)*100}`) 
  const threeRating    = parseInt(`${(_.get(averageRating, "three_star", 0)/count_rating)*100}`)
  const twoRating      = parseInt(`${(_.get(averageRating, "two_star", 0)/count_rating)*100}`)
  const oneRating      = parseInt(`${(_.get(averageRating, "one_star", 0)/count_rating)*100}`) 
 
  const renderAverageRating = () => {
    const total = ratingsChannel.length
    const arrTotalRating = [
      { width: `${fiveRating}%` },
      { width: `${fourRating}%` },
      { width: `${threeRating}%` },
      { width: `${twoRating}%` },
      { width: `${oneRating}%` },
    ]
    return (
      <div className={classes.averageRating}>
        <div className={classes.averageRatingTotal}>
          <p className={classes.averageRatingLerver}>{average_point}.0</p>
          <Grid container alignItems="center" justify="center" spacing={0} style={{ marginTop: "-12px" }}>
            <Grid item xs={8}>
              <Rating disabled name="simple-controlled" value={5} style={{ color: "#045EA8" }}/>
            </Grid>
            <Grid item xs={4}><Typography variant="h5">{total}</Typography></Grid>
          </Grid>
        </div>
        <div className={classes.averageRatingDescribe}>
          {arrTotalRating.map((item: any, index: number) => {
            return (
              <div>
                <div style={{ float: 'left', width: "40px", height: 20, marginTop: "-4px" }}>
                  {5 - index}, <GradeIcon style={{ display: "none" }} />
                </div>
                <div className={classes.averageRatingDescribeFive} key={index} style={{ width: "calc(100% - 40px )" }}>
                  <div className={classes.averageRatingDescribeFiveLerver} style= {{ width: item.width }}></div>
                </div>
              </div>
            )
          })}
        </div>
        <div style={{ clear: "both" }}></div>
      </div>
    )
  }

  return (
    <div className={classes.rootRating}>
      <Typography variant="h5" className={classes.titleRating}>Đánh giá chất lượng</Typography>
      {renderAverageRating()}
      <div className={classes.ratingList}>
        <Typography variant="h5" className={classes.titleRating}>Đánh giá gần nhất</Typography>
        {Array.isArray(ratings) ? renderRatingChanel() : <Typography align="center">Không có đánh giá nào</Typography> }
        {/* {Array.isArray(ratings) ? renderRating() : <Typography align="center">Không có đánh giá nào</Typography> } */}
      </div>
    </div>
  );
};


export default Ratings;
