import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { contentAccessOne, contentAccessTwo, postsInfo } from "./Constants";

const Mission = () => {
  
  const renderContent = (element: any) => {
    return (
      <Grid item md={6}>
        <Typography variant="h5" style={{ padding: "20px 0px", fontWeight: 700 }}>
          {element.title}
        </Typography>
        <Typography style={{}}>{contentAccessOne}</Typography>
        <Typography style={{}}>{contentAccessTwo}</Typography>
      </Grid>
    )
  };
  
  const renderImage = (element: any) => {
    return (
      <Grid item md={6}>
        <img src={element.image} />
      </Grid>
    )
  };

  return (
    <div className={"mission-area body-general"}>
      {postsInfo.map((element: any, index: number) => {
        return (
          <div style={{ padding: "20px 0px" }} >
            { element.type === 1 
              ? <Grid container direction="row" spacing={3} alignItems={"center"}>
                  {renderContent(element)}
                  {renderImage(element)}
                </Grid>
              : <Grid container direction="row" spacing={3} alignItems={"center"}>
                  {renderImage(element)}
                  {renderContent(element)}
                </Grid>
            }
          </div>
        )
      })}
    </div>
  );
};

export default Mission;

