import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { contentAccessOne, contentAccessTwo, postsInfo } from "./Constants";
import { makeStyles } from "@material-ui/core/styles"

const indexStyles = makeStyles((theme) => ({
  paddingPostsInfo: {
    padding: "20px 0px",
    [theme.breakpoints.down("xs")]: {
      padding: "0px 0px",
    },
  },
  introduceTitle: {
    padding: "20px 0px",
    fontWeight: 700,
    [theme.breakpoints.down("xs")]: {
      padding: "8px 8px",
      fontWeight: 700,
      textAlign: "left",
    },
  },
  introduceContent: {
    textAlign: "justify",
    [theme.breakpoints.down("xs")]: {

    },
  }
}));


const Mission = () => {
  const classes = indexStyles();
  const renderContent = (element: any) => {
    return (
      <Grid item md={6}>
        <Typography variant="h5" className={classes.introduceTitle} >
          {element.title}
        </Typography>
        {element.contents.map((content: any, count: number) => <Typography key={count} className={classes.introduceContent}>{content.item}</Typography>)}
      </Grid>
    )
  };
  
  const renderImage = (element: any) => {
    return (
      <Grid item md={6}>
        <img src={element.image} />
      </Grid>
    )
  };

  return (
    <div className={"mission-area body-introduce"}>
      {postsInfo.map((element: any, index: number) => {
        return (
          <div className={classes.paddingPostsInfo}>
            { element.type === 1 
              ? <Grid container direction="row" spacing={1} alignItems={"center"}>
                  {renderContent(element)}
                  {/* {renderImage(element)} */}
                </Grid>
              : <Grid container direction="row" spacing={1} alignItems={"center"}>
                  <Hidden smDown>
                    <>
                      {renderImage(element)}
                      {renderContent(element)}
                    </>
                  </Hidden>
                  <Hidden mdUp>
                    <>
                      {renderContent(element)}
                      {renderImage(element)}
                    </>
                  </Hidden>
                </Grid>
            }
          </div>
        )
      })}
    </div>
  );
};

export default Mission
