import React, { FunctionComponent } from 'react'
import { InputBase, Button, Paper, Divider, IconButton } from '@material-ui/core'
import MenuIcon from '@material-ui/icons/Menu'
import SearchIcon from '@material-ui/icons/Search'
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment'
import AccountCircle from '@material-ui/icons/AccountCircle'
import DirectionsIcon from '@material-ui/icons/Directions'
import CloseIcon from '@material-ui/icons/Close';
import Autocomplete from '@material-ui/lab/Autocomplete'
import TextField from '@material-ui/core/TextField'
import Validator from "../../modules/validator";
import { localDataSearch } from "../../constants";
import { makeStyles, Theme } from '@material-ui/core/styles';


const useStyles = makeStyles((theme: Theme) => ({
  root: {
    padding: '0px 0px',
    boxShadow: "0px",
    display: 'flex',
    width: "600px",
    maxWidth:  "900px",
  },
  textFieldSearch: {
    width: "600px"
  },
  paper: {
    maxHeight: "55vh",
    lineHeight: 1,
    fontSize: "15px",
    color: "red",
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
  },
  iconButton: {
    padding: 10,
  },
  divider: {
    height: 28,
    margin: 4,
  },
}))



interface PropsData {

}
// const dataSearch = new Array()
// localStorage.setItem(localDataSearch, JSON.stringify(dataSearch))
// let theme = createMuiTheme({
//   overrides: {
//     MuiAutocomplete: {   
//       paper: {  
//         color: 'red',
//         lineHeight: 1,
//         maxHeight: "80vh",
//         fontSize: "15px"
//       },
//       option: {
//         minHeight: "30px",
//       }
//     },
//   },
// });

const SearchForm: FunctionComponent<PropsData> = (props: PropsData) => {
  const classes = useStyles()
  const [valueSearch, setValueSearch] = React.useState("") 

  let dataProject = [
    { title: "Amata City Long Thành", index: 0},
    { title: "Hội An Golden Sea", index: 1 },
    { title: "Civilized Center City", index: 2 },
    { title: "Ecopark Hải Dương - Ecorivers", index: 3 },
    { title: "TD Plaza Cần Thơ", index: 4 },
    { title: "DC Tower", index: 5 },
    { title: "Long Thành Airport City", index: 6 },
    { title: "Khu đô thị Rùa Vàng City", index: 7 },
    { title: "Avalon Saigon Apartments", index: 8 },
    { title: "Khu phố chợ Điện Nam Bắc", index: 9 },
    { title: "Khu đô thị Sao Mai Xuân Thịnh", index: 10 },
    { title: "Eco Smart City", index: 11 },
  ]
  const data = [
    { title: "Túi sách hàng hiệu nhật", year: 101 },
    { title: "Túi sách hàng hiệu hàn quốc", year: 102 },
    { title: "cặp sách đi học", year: 103 },
    { title: "Túi kéo", year: 104 },
    { title: 'Túi thời trang', year: 105 },
  ]

  const onChangeSearch = (e: any) => {
    setValueSearch(e.target.value)
  }

  // let dataSearch = JSON.parse(localStorage.getItem(localDataSearch) as string) 
  // if(!Validator.isBlank(localStorage.getItem(localDataSearch))) {
  //   localStorage.setItem(localDataSearch, JSON.stringify(dataSearch))
  // }

  const onBlur = (e: any) => {
    // if(e.target.value){
    //   dataSearch.unshift({ title: e.target.value, year: dataSearch.length + 1 })
    //   if(dataSearch.length == 4){
    //     dataSearch.splice(3, 1)        
    //   }
    // }
    // localStorage.setItem(localDataSearch, JSON.stringify(dataSearch))
  }

  return (
    <div  className={classes.root} style={{ display: "flex" }} > 
      {/* <ThemeProvider theme={theme}> */}
      {/* <ThemeProvider> */}
        <Autocomplete
           
          freeSolo // Clear icon show
          autoHighlight={true} // auto hover first item or select item last
          // autoSelect={true} // Auto Select item
          // selectOnFocus={true}
          // popupIcon={<SearchIcon />} // Icon select show data
          closeIcon={false} //{<CloseIcon fontSize="small" />} clear icon show data
          // options={ valueSearch ? dataProject :  dataSearch.concat(data)}
          options={data}
          getOptionLabel={option => option.title}
          size="small"
          
          renderInput={ params => 
            <TextField 
              {...params} 
              className={classes.textFieldSearch}
              variant="outlined"
              onChange={(e: any) => onChangeSearch(e)}
              onBlur={(e: any) => onBlur(e)}
            /> 
          }
        />
      {/* </ThemeProvider> */}
      <Button size="small" color="primary" className={classes.iconButton} aria-label="directions" >
        <SearchIcon />Tìm kiếm
      </Button>
    </div>
  )
}

export default SearchForm
