import React, {FunctionComponent} from "react"
import { Grid, Button, IconButton, Typography} from "@material-ui/core"
import Icon from '@material-ui/core/Icon';
import StringProcess from '../../modules/stringProcess';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import SmartphoneIcon from '@material-ui/icons/Smartphone';
import LocalPhoneIcon from '@material-ui/icons/LocalPhone';
import WebAssetIcon from '@material-ui/icons/WebAsset';
import WebIcon from '@material-ui/icons/Web';
import EmailIcon from '@material-ui/icons/Email';
import PhoneIphone from "@material-ui/icons/PhoneIphone";

interface InvestorProp {
  id: string
  name: string
  description: string
  slug: string
  email: string
  website: string
  image: string
  address: {
   text: string
  }
  phone: string
  mobile: string
}
 
interface ProjectDetailsProp {
  investor: InvestorProp
  slug: string
}

const DetailInvestor: FunctionComponent<ProjectDetailsProp> = (props: ProjectDetailsProp) => {
  const freshText = (input: string) => {
    input = StringProcess.stripProperties(input, ['href', 'src', 'rel']);
    input = StringProcess.stripTags(input, '<a><br/><br><img><p><h1><h2><h3><h4><h5><u><img><video><audio><li><ul><table><tr><td><sup><sub>');
    input = input.replace(/https:\/\/nhadatmoi.net\/data/g, 'https://static.nhadatmoi.net/data');
    input = input.replace(/&nbsp;/g, ' ');
    return input;
  };

  let description = freshText(String(props.investor.description));
  return (
    <Grid container spacing={4} direction="row" justify="flex-end" alignItems="flex-end">
      <Grid 
        item xs={12} 
        className="investor-homepage"
        style={{
          // backgroundImage: 'url(' + props.investor.image + ')'
          backgroundImage: "url('https://static.nhadatmoi.net/default/background/image_us-min.jpg')"
        }} 
      
      >
      </Grid>
      <Grid item xs={1}></Grid>
      <Grid item xs={3}>
        <div className="investor-homepage__infopublic">
          <h3 className="project-info-title text-active u-margin-bottom-small">Thông tin công ty</h3>
          <div className="investor-area__title__information__light">
            <div className="investor-area__title__information__light__svg">
              <svg viewBox="0 0 384 512">
                <path d="M172.268 501.67C26.97 291.031 0 269.413 0 192 0 85.961 85.961 0 192 0s192 85.961 192 192c0 77.413-26.97 99.031-172.268 309.67-9.535 13.774-29.93 13.773-39.464 0zM192 272c44.183 0 80-35.817 80-80s-35.817-80-80-80-80 35.817-80 80 35.817 80 80 80z"/>
              </svg>
            </div>
            <p>{props.investor.address.text ? props.investor.address.text : "Đang cập nhật"}</p>
          </div>
          <div className="investor-area__title__information__light">
            <div className="investor-area__title__information__light__svg">
              <svg viewBox="0 0 512 512">
                <path d="M493.4 24.6l-104-24c-11.3-2.6-22.9 3.3-27.5 13.9l-48 112c-4.2 9.8-1.4 21.3 6.9 28l60.6 49.6c-36 76.7-98.9 140.5-177.2 177.2l-49.6-60.6c-6.8-8.3-18.2-11.1-28-6.9l-112 48C3.9 366.5-2 378.1.6 389.4l24 104C27.1 504.2 36.7 512 48 512c256.1 0 464-207.5 464-464 0-11.2-7.7-20.9-18.6-23.4z"/>
              </svg>
            </div>
            <p>{props.investor.phone ? props.investor.phone : 'Đang cập nhật'} {props.investor.mobile ? ` - ${props.investor.mobile}` : ""}</p>
          </div>
          <div className="investor-area__title__information__light">
            <div className="investor-area__title__information__light__svg">
              <svg viewBox="0 0 512 512">
                <path d="M502.3 190.8c3.9-3.1 9.7-.2 9.7 4.7V400c0 26.5-21.5 48-48 48H48c-26.5 0-48-21.5-48-48V195.6c0-5 5.7-7.8 9.7-4.7 22.4 17.4 52.1 39.5 154.1 113.6 21.1 15.4 56.7 47.8 92.2 47.6 35.7.3 72-32.8 92.3-47.6 102-74.1 131.6-96.3 154-113.7zM256 320c23.2.4 56.6-29.2 73.4-41.4 132.7-96.3 142.8-104.7 173.4-128.7 5.8-4.5 9.2-11.5 9.2-18.9v-19c0-26.5-21.5-48-48-48H48C21.5 64 0 85.5 0 112v19c0 7.4 3.4 14.3 9.2 18.9 30.6 23.9 40.7 32.4 173.4 128.7 16.8 12.2 50.2 41.8 73.4 41.4z"/>
              </svg>
            </div>
            <p>{props.investor.email ? props.investor.email : "Đang cập nhật"}</p>
          </div>
          <div className="investor-area__title__information__light">
          <Typography variant="subtitle2">
            Website: { props.investor.website  ? <a href={`${props.investor.website}`} target="_blank">{props.investor.website}</a> : "Đang cập nhật"}
          </Typography>
          </div>
        </div>
      </Grid>
      <Grid item xs={7}>
        <Grid container spacing={2}>
          <Grid item xs={2} >
            <div className="investor-area__avata" >
              <img 
                src={props.investor.image} 
                alt="nhadatmoi.net" 
              />
            </div>
          </Grid>
          <Grid item xs={10}>
            <h1 className="title-project__title">{props.investor.name}</h1>
          </Grid>
        </Grid>
        {
          props.investor.description &&
            <div 
              className="project-info__description u-margin-bottom-medium" 
              dangerouslySetInnerHTML={{ __html: description }} 
            />
        }
      </Grid>
      <Grid item xs={1}></Grid>
    </Grid>
  )
}

export default DetailInvestor
