import React, { useState, useEffect, useRef } from 'react';
import Popper, { PopperPlacementType } from '@material-ui/core/Popper';
import { ThemeProvider } from '@material-ui/core/styles';
import { Link, Element , Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll';
import _ from 'lodash'
import { 
  Grid, 
  InputBase, 
  Fade,
  Typography, 
  Paper, 
  CardHeader,
  CardActionArea, 
  CardMedia, 
  Avatar, 
  TextField,
  Badge, 
  Divider, 
  Input, 
  Chip, 
  IconButton
} from '@material-ui/core';
import SearchIcon from '@material-ui/icons/Search';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import TagFacesIcon from '@material-ui/icons/TagFaces';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import CloseIcon from '@material-ui/icons/Close';
import SendIcon from '@material-ui/icons/Send';
import { 
  Theme, 
  makeStyles, 
} from '@material-ui/core/styles';
import { 
  dataEmoji, 
  arrChat, 
  giphyDefault, 
  useStyles,
  themeTextfieldAdmin, 
  themeTextfieldUser, 
  StyledBadge,
  themeTextHeader,
  themeInput,
  themeInputBase,
  zIndexMax,
  useStylesBootstrap, 

} from "./constant"
import { create } from 'jss';
import rtl from 'jss-rtl';
import axios, {Method} from "axios";
import {apiBaseURL, apiGraphQLURL} from "../../constants";
import "cross-fetch/polyfill";
import Tooltip, { TooltipProps } from '@material-ui/core/Tooltip';
import ApolloClient from "apollo-boost";
import {apolloClient} from "../../utils";
const client = axios.create({ baseURL: apiBaseURL, timeout: 30000 });
import { SEN_MESSAGE, UPDATE_MESSAGE, READ_MESSAGE } from "../../graphql/chat/mutation";
import moment from "moment"
import 'moment/locale/es'   
moment.locale('vi')
import { StylesProvider, jssPreset } from '@material-ui/core/styles';
const jss = create({ plugins: [...jssPreset().plugins, rtl()] });

function BootstrapTooltip(props: TooltipProps) {
  const classes = useStylesBootstrap()
  return <Tooltip arrow classes={classes} {...props} />;
}

interface ChatMessage {
  chatMessage: {
    id: string
    conversation: Conversation[]
  }
}

interface Conversation {
  id: string
  send_user_id: string
  message: {
    text: string
    attachment: {
      type: string
      payload: {
        url: string
      }
    }
    status_check: number
    read_at: string
    send_at: string
  }
}


export default function PositionedPopper() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState<HTMLButtonElement | null>(null);
  const [open, setOpen] = React.useState(false);
  const [placement, setPlacement] = React.useState<PopperPlacementType>();
  const [online, setOnline] = React.useState<boolean>(true);
  const [internet, setInternet] = React.useState<boolean>(true);
  const [texting, setTexting] = React.useState<boolean>(false);
  const [giphyData, setGiphyData] = React.useState<any>();
  const [openEmojiGiphy, setOpenEmojiGiphy] = React.useState<boolean>(false);
  const [valueSearchGiphy, setValueSearchGiphy] = React.useState("");
  const [avatarImage, setAvatarImage] = React.useState("https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot")
  // const [avatarImage, setAvatarImage] = React.useState("https://png.pngtree.com/element_origin_min_pic/17/01/03/643d0060ffc94e7bd0d6aadc783274d8.jpg")
  const [nameAdmin, setNameAdmin] = React.useState("Nguyễn Thanh Hương");
  let   [valueInput, setValueInput] = React.useState("");
  const [viewEmoji, setViewEmoji] = React.useState(false);
  const [chatMessage, setChatMessage] = React.useState<ChatMessage>()
  const [reload, setReload] = React.useState(true)
  const [checkLogin, setCheckLogin] = React.useState<boolean>(true)

  const onCloseChat = () => {
    setOpen(false)
  };

  const getCookie = (cname: any) => {
    var name = cname + "=";
    var ca = document.cookie.split(';');
    for(var i = 0; i <ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0)==' ') {
            c = c.substring(1);
        }
        if (c.indexOf(name) == 0) {
            return c.substring(name.length,c.length);
        }
    }
    return "";
  }

  const checkCookie = () => {
    let user = getCookie("user");
    if (user != "") {
      setCheckLogin(true)
    } else {
      setCheckLogin(false)
    }
  }

  const _senMessage = async (value: any, url: string)=> { 
    apolloClient.mutate({mutation: READ_MESSAGE, variables: {
      "input": {
        "id": "5e991fef5e32af5f4e43c1ef",
        "message": {
          "text": `${value}`,
          "attachment": {
            "type": "image",
            "payload": {
              "url" : `${url}`
            }
          }
        }
      }}})
      .then((response: any) => {
      if(response.data){
        setChatMessage(response.data.chatMessage)
      }
    })
    setReload(!reload)
    setValueInput("")
    setScrollTo()
  };

  const renderHeader = () => {
    return (
      <div >
        {/* <ThemeProvider theme={themeTextHeader}> */}
          <CardHeader
            className={classes.header}
            avatar={
              <StyledBadge
                overlap="circle"
                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                variant= { online ? "dot" : "standard" }
              >
                <Avatar src={avatarImage} className={classes.sizeAvatarSmall} alt="nhadatmoi.net" />
              </StyledBadge>
            }
            title={nameAdmin}
            subheader={ 
              <Typography variant="caption" align="center" className={classes.timeWork}>
                Hoạt động: {moment().format('DD/MM/YYYY')}
              </Typography>
            }
            action={
              <IconButton color='inherit' aria-label="settings" onClick={() => onCloseChat()} >
                <CloseIcon/>
              </IconButton>
            }
          />
        {/* </ThemeProvider> */}
      </div>
    )
  };

  const searchGiphy = async (event: any)=>  { 
    setValueSearchGiphy(event.target.value)
    let url = `https://api.giphy.com/v1/gifs/search?api_key=UgTMyvovmg74m8fPN6qYiB623nwS1IPc&q=${event.target.value}&limit=20&offset=0&rating=G&lang=vi`
    let getData = await axios.get(url, {})
    .then(function (response) {
      setGiphyData(response.data.data)
    })
  };

  let dataSearch: any = [];
  if(Array.isArray(giphyData)){
    giphyData.map((item: any, index: number) => {
      dataSearch.push({ 
        image: {
          url: item.images.fixed_width.url,
          height: item.images.fixed_width.height,
          width: item.images.fixed_width.width,
        }})
    })
  };
  
  // Gửi ảnh emoji giphy 
  const setItemGiphy = (index: number, name: string) => {
    setOpenEmojiGiphy(!openEmojiGiphy)
    if(name == "giphyDefault") {
      _senMessage("", giphyDefault[index].image.url)
    } else if ( name == "dataSearch") {
      _senMessage("", dataSearch[index].image.url)
    }
  };

  const onClickEmoji = (index: number) => {
    valueInput = valueInput +  dataEmoji[index].native
    setValueInput(valueInput)
  };

  const renderEmoji = () => {
    return ( 
      <div className={classes.overflowHeightEmoji}>
        {dataEmoji.map((item: any, index: number) => {
          return (
            <div 
              onClick={() => onClickEmoji(index)} 
              key={index} 
              className={classes.itemEmoji}
            >{item.native}</div>
          )
        })}
      </div>
    )  
  }

  const renderGiphy = () => {
    return (
      <div className={classes.viewWidthGiphy} >
        <div className={classes.searchGiphy}>
          <SearchIcon className={classes.sizeSearchIcon} />
          <ThemeProvider theme={themeInputBase}>
            <InputBase
              onChange={(e) => searchGiphy(e)}
              className={classes.inputBaseGiphy}
              placeholder="Tìm kiếm ..."
            />
          </ThemeProvider>
        </div>
        <div className={classes.viewSizeGiphy}>
          {valueSearchGiphy 
            ? dataSearch.map((item: any, index: number) => {
                return (
                  <div className={classes.itemGiphy} >
                    <img 
                      className={classes.imgListGiphy} 
                      alt="giphy" 
                      src={item.image.url} 
                      onClick={() => setItemGiphy(index, "dataSearch")} 
                    />
                  </div>
                )
              })
            : giphyDefault.map((item: any, index: number) => { // view giphy default
                return (
                  <div className={classes.itemGiphy} >
                    <img 
                      className={classes.imgListGiphy} 
                      alt="giphy" 
                      src={item.image.url} 
                      onClick={() => setItemGiphy(index, "giphyDefault")} 
                    />
                  </div>
              )})
          }
        </div>
      </div>
    )
  }

  const renderEmojiGiphy = () => {
    return (
      <div className={classes.emojiGiphy}>
        <div className={classes.marinButtonEmojiGiphy}>
          <button 
            className={classes.buttonEmojiGiphy} 
            onClick={() => setViewEmoji(true)} 
            style={{ backgroundColor: !viewEmoji ? "#aaa" : "" }}
          > Biểu tượng</button>
          <button 
            className={classes.buttonEmojiGiphy} 
            onClick={() => setViewEmoji(false)} 
            style={{ backgroundColor: viewEmoji ? "#aaa" : "" }}
          > Ảnh động</button>
        </div>
        { viewEmoji ? renderEmoji() : renderGiphy()  }
      </div>
    )
  };

  const doesConnectionExist = () => {
    let xhr = new XMLHttpRequest();
    let file = "https://nhadatmoi.net/";
    let randomNum = Math.round(Math.random() * 10000);
    xhr.open('HEAD', file + "?rand=" + randomNum, false);
    try {
      xhr.send();
      if (xhr.status >= 200 && xhr.status < 304) {
        setOnline(true)
        return true;
      } else {
        setOnline(false)
        return false;
      }
    } catch (e) {
        return false;
    }
  }

  const renderContentChat = () => {
    let conversation = _.get(chatMessage, "conversation", [])
    console.log("conversation", conversation)
    return (
      <div className={classes.overflowHeightEmojiGiphy} style={{ height: openEmojiGiphy ? '230px' : '430px' }} id={'ContainerElementID'}>
        <Element name="myScrollToElement" id={"test_ndm"}>
          <Typography align="center" className={classes.textSizeNewDate}>
            {moment().format('LLL')}
          </Typography>
          <Divider />
          {conversation.length ?
            conversation.map((item: any, index: number) => {
              let checkNewDate = false
              let setViewAvatar = true

              let oldDate = index > 0 ? moment(Number(conversation[index - 1].send_at)).format('YYYY-MM-DD') : ''
              let newDate = moment(Number(conversation[index].send_at)).format('YYYY-MM-DD')
              if(index > 0 && (oldDate !== newDate)) {
                checkNewDate = true
              }

              // setView avatar admin
              // if(item.type === 'admin' && index > 0) {
              //   if((index === arrChat.length) || (arrChat[index - 1].type != arrChat[index].type) || (oldDate !== newDate)){
              //     setViewAvatar = true
              //   }
              // } else if(item.type === 'admin' && index === 0 ) {
              //   setViewAvatar = true
              // }
              // let width = item.image.width
              // let height = item.image.height

              return (
                <div key={index} style={{ display: "block" }}>
                  { checkNewDate && <Typography align="center" className={classes.textSizeNewDate}>
                    {moment(Number(item.send_at)).format("LLL")} <Divider /></Typography>}
                  { item.send_user_id == 'admin'
                    ? <ThemeProvider theme={themeTextfieldAdmin}>
                      <div className={classes.viewAvatarChatContent}>
                        { setViewAvatar && <Avatar className={classes.avatarAdminText} alt="nhadatmoi.net" src={avatarImage} /> }
                      </div>
                      <div className={classes.textFieldChatContent}>
                        {
                          item.message.attachment.payload.url
                          ? <div className={classes.viewContentImageGiphy} style={{ width: `250px`, maxWidth: "300px", height: `auto`,   float: "left" }} >
                              <BootstrapTooltip  title={moment(Number(item.send_at)).fromNow()} placement="top"> 
                                <img className={classes.imgGiphy} alt="giphy" src={item.message.attachment.payload.url} />
                              </BootstrapTooltip>
                            </div>
                          : <div className={classes.textAdminLeft}>
                              <BootstrapTooltip  title={moment(Number(item.send_at)).fromNow()} placement="top">
                                <TextField
                                  fullWidth={item.message.text.length > 15 ? true : false}
                                  label=""
                                  color="primary"
                                  className={classes.textAdmin}
                                  value={item.message.text.trim()}
                                  margin="dense"
                                  id="outlined-basic"
                                  multiline={true}
                                  InputProps={{
                                    readOnly: true
                                  }}
                                  variant="outlined"
                                />
                                </BootstrapTooltip >
                            </div>
                        }
                      </div>
                    </ThemeProvider>
                    : <ThemeProvider theme={themeTextfieldUser}>
                      {
                        item.message.attachment.payload.url
                        ? <div className={classes.viewContentImageGiphy} style={{ width: `250px`, height: `auto`, float: "right" }} >
                            <BootstrapTooltip  title={moment(Number(item.send_at)).fromNow()} placement="top"> 
                              <img className={classes.imgGiphy} alt="giphy" src={item.message.attachment.payload.url} />
                            </BootstrapTooltip >
                          </div>
                        : <div className={classes.textFieldUserChatContent} dir={`${item.message.text.length < 45 ? 'rtl' : ""}`}>
                            <BootstrapTooltip  title={moment(Number(item.send_at)).fromNow()} placement="top">
                              <TextField
                                fullWidth={item.message.text.length > 15 ? true : false}
                                label=""
                                color="primary"
                                className={classes.textUser}
                                value={item.message.text.trim()}
                                margin="dense"
                                id="outlined-basic"
                                multiline={true}
                                InputProps={{
                                  readOnly: true
                                }}
                                variant="outlined"
                              />
                            </BootstrapTooltip>
                          </div>
                      }
                    </ThemeProvider>
                  }
                  <div style={{ clear: "both" }} />
                </div>
              )
            })
          : ""}
          <div>
            {!internet && <p className={classes.errInternet}> <u>Không thể gửi tin nhắn này. Kiểm tra kết nối internet và thử lại</u></p> }
          </div>
          <div>
            {texting && <p className={classes.texting}>Đang nhập ...</p>}
          </div>
        </Element>
      </div>
    )
  };

  const onClickOpenEmojiGiphy = () => {
    setOpenEmojiGiphy(!openEmojiGiphy) 
    setViewEmoji(true)
  };

  React.useEffect(() => {
    checkCookie()
    // const interval = setInterval(() => {
    //   let internet = doesConnectionExist()
    //   setInternet(!internet)
    // }, 5000);
    // return () => clearInterval(interval);
  }, [])
 
  const apiBaseURL = "https://graph.nhadatmoi.net";
  const apiUpload = `${apiBaseURL}/private_upload_image_app`;
  const api = (method: Method, url: string, data?: any) => client
    .request({
      data: JSON.stringify(data), 
      headers: { 
        "Accept": "application/json",
        "Content-Type": "application/json",
      }, 
      method,
      url,
      withCredentials: true,
    }).then((response: any) => response.data);

  const upLoadImage = (files: any) => {
    const reader = new FileReader;
    let file = files.target.files[0]
    reader.readAsDataURL(file)
    reader.onload = () => {
      api("POST", `${apiUpload}`, { 
        base64_image: reader.result, 
        fileName: (new Date()).getTime() + "" + file.name,
        typeUpload: "ad_sell_lease",
      })
      .then(function (res) {
        _senMessage("", res.location)
      })
      .catch(function (err) {
        alert('HTTP Error: ' + err.message);
      });
    };
  };

  const setOnKeyDown = (event: any) => {
    if(event.keyCode == 13){
      _senMessage(valueInput, "")
      setValueInput("");
      setScrollTo()
    }
  };

  const setScrollTo = () => {
    let heightChatDiv = 0;
    let elm = document.getElementById("test_ndm");
    if (elm) {
      heightChatDiv =  elm.offsetHeight;
    }
    scroller.scrollTo('myScrollToElement', {
      duration: 500,
      delay: 100,
      smooth: true,
      containerId: 'ContainerElementID',
      offset: heightChatDiv,
    })
  }
  
  const setChangeInput = (value: any) => {
    setValueInput(value)
  };

  const renderInput = () => {
    return (
      <Paper component="form" className={classes.inputBase}>
        <ThemeProvider theme={themeInput}>
          <Input 
            fullWidth
            disabled={!internet}
            className={classes.inputText}
            margin="dense"
            id="outlined-basic" 
            autoFocus={true}
            value={valueInput}
            multiline={true}
            onChange={(e) => setChangeInput(e.target.value)}
            onKeyDown={(e) => setOnKeyDown(e)}
            placeholder="Nhập tin nhắn ..."
            autoComplete="false"
          />
          { openEmojiGiphy 
            ? <BootstrapTooltip  title="Chèn biểu tượng" placement="top"> 
                <ExpandMoreIcon className={classes.sizeIcon} onClick={onClickOpenEmojiGiphy} color="primary" />
              </BootstrapTooltip > 
            : <BootstrapTooltip  title="Chèn biểu tượng" placement="top"> 
                <TagFacesIcon className={classes.sizeIcon} onClick={onClickOpenEmojiGiphy} color="primary" />
              </BootstrapTooltip >
          }
          { valueInput
            ? <BootstrapTooltip  title="Gửi tin nhắn" placement="top">
                <SendIcon onClick={() => _senMessage(valueInput, "")} className={classes.sizeIcon} color="primary" />
              </BootstrapTooltip >
            : <BootstrapTooltip  title="Thêm tập tin" placement="top">
                <div className={classes.inputWrapper}> 
                  <AddCircleOutlineIcon className={classes.sizeIcon} color="primary"/> 
                  <input type="file" className={classes.fileInput}  onChange={upLoadImage} /> 
                </div>
              </BootstrapTooltip >
          }
        </ThemeProvider>
      </Paper>
    )
  };

  const viewChat = () => {
    let _open = open && checkLogin ? true : false
    return (
      <Popper open={_open} anchorEl={anchorEl} placement={placement} transition >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={300}>
            <Paper>
              <div className={classes.viewChat} > 
                {renderHeader()}
                {renderContentChat()}
                {openEmojiGiphy && renderEmojiGiphy()}
                {renderInput()}
              </div>
            </Paper>
          </Fade>
        )}
      </Popper>
    )
  };

  const handleClick = (newPlacement: PopperPlacementType) => {
    setOpen(true)
    setPlacement(newPlacement);
  };

  const renderAvatar = (avatar: string, name: string, index: number) => {
    return (
      <div
        style={{ bottom: `${index*65 + 80}px` }}
        className={avatar === avatarImage ? classes.renderAvatarBorderRadius : classes.renderAvatar}
        onClick={() => setAvatarImage(avatar) }
      >
        <BootstrapTooltip title={name} placement="left">
          <StyledBadge
            overlap="circle"
            anchorOrigin={{ vertical: 'bottom', horizontal: 'right', }}
            // variant= { online ? "dot" : "standard" }
            variant="dot"
          >
            <Avatar src={avatar} onClick={() => handleClick("top-start")} className={classes.large} alt="nhadatmoi.net"/>
          </StyledBadge>
        </BootstrapTooltip>
      </div>
    )
  };

  let arrAvatar = [
    { name: "Hạnh Nhi", avatar: "https://thuthuatnhanh.com/wp-content/uploads/2019/07/anh-girl-xinh-facebook-tuyet-dep-387x580.jpg"},
    { name: "Thanh Hoa", avatar: "https://2.bp.blogspot.com/-5bGrIrLVsms/WnFiwDBKgYI/AAAAAAAAHGs/KPp1IDsgD9AumXTY9Ehh31pZ0p04cHDKQCLcBGAs/s1600/girl-xinh-hvnh-2.jpg"},
    { name: "Hồng Thắm", avatar: "https://lh3.googleusercontent.com/proxy/IY9QOJfFfwVvSLLKU04d2be4ofgqg2bQFcjVN7ZCuICRNjc8ZaRGL5KpoHW89v8zJSag9r1o3YNapU8uVSbQfVhRI9pmC5hCS-XYpOyMzyVm7ALLJFL_sWxNlxxWdExzfmW2HN02PJHOpd-NiAssc3Au1vr4"},
    { name: "Dương Yến", avatar: "https://phunugioi.com/wp-content/uploads/2020/03/hinh-anh-gai-xinh-hot-girl-de-thuong-cute-nhat.jpg"},
    { name: "Minh Hạnh", avatar: "https://tylebongdahomnay.com/wp-content/uploads/2019/03/%E1%BA%A2nh-G%C3%A1i-Xinh-Vi%E1%BB%87t-Nam3.png"},
    { name: "Dương Minh yến", avatar: "https://hicksartgallery.com/wp-content/uploads/2019/09/avatar-gai-xinh.jpg" },
  ]

  return (
    <div className={classes.root}>
      {viewChat()}
      <Grid container justify="center">
        <Grid item>
          {/* { !open && renderAvatar()} */}
          {
            arrAvatar.map((item: any, index: number) => {
              return  renderAvatar( item.avatar, item.name, index)
            })
          }
        </Grid>
      </Grid>
    </div>
  );
};
