import React, { Component } from 'react';
import Popper, { PopperPlacementType } from '@material-ui/core/Popper';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDial, { SpeedDialProps } from '@material-ui/lab/SpeedDial';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';
import {
  Grid,
  Button,
  Fade,
  Typography,
  Paper,
  Card, 
  CardHeader,
  CardContent,
  CardActionArea, 
  CardMedia,
  Avatar,
  TextField,
  Badge,
  Divider,
  Tooltip,
  Input
} from '@material-ui/core';
import InputBase from '@material-ui/core/InputBase';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import TagFacesIcon from '@material-ui/icons/TagFaces';
import DirectionsIcon from '@material-ui/icons/Directions';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import CloseIcon from '@material-ui/icons/Close';
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

import { Theme, makeStyles, withStyles, createStyles } from '@material-ui/core/styles';

const theme = createMuiTheme({
  overrides: {
    MuiTextField: {
      root: {
        border: "0px",
        borderRadius: "0px"
        // borderRadius: "0px",
      }
    },
  },
});

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: 550,
      zIndex: 900,
    },
    textAdmin: {
      borderRadius: "8px!important",
      backgroundColor: "#1a75ff",
      fontSize: "13px",
      color: "white",
      border: "1px "
    },
    textUser: {
      borderRadius: "8px!important",
      fontSize: "13px",
    },
    inputBase: {
      padding: '2px 4px',
      display: 'flex',
      alignItems: 'center',
      width: "100%",
      position: "absolute",
      boxShadow: "0px -1px 3px black",
      // borderTop: "3px solid gray",
      borderRadius: "0px 0px",
      bottom: "0px"
    },
    input: {
      marginLeft: theme.spacing(1),
      height: 50 ,
      flex: 1,
    },
    divider: {
      height: 28,
      margin: 4,
    },
    typography: {
      padding: theme.spacing(2),
    },
    positionSizeViewChat: {
      backgroundColor: "#fff2e6",
      zIndex: 900,
      height:"550px", 
      width: "350px",
      position: "fixed",
      right: '30px',
      bottom: "70px",
      boxShadow: "0px -1px 3px #b3c5cc",
    },
    textField: {
      position: "absolute",
      bottom: "0px"
    },
    exampleWrapper: {
      position: 'fixed',
      right: "60px", 
      bottom: "2px",
    },
    large: {
      width: theme.spacing(7),
      height: theme.spacing(7),
    },
    sizeAvatarSmall: {
      width: theme.spacing(5),
      height: theme.spacing(5),
    },
    positionAvatar: {
      position: 'fixed',
      right: "15px", 
      bottom: "80px",
    },
    radioGroup: {
      margin: theme.spacing(1, 0),
    },
    avatar: {
      backgroundColor: "blue",
    },
    media: {
      height: 100,
      backgroundColor: "blue"
    },
    hearder: {
      backgroundColor: "#1a75ff",
      color: "white"
    },
    speedDial: {
      position: 'absolute',
      '&.MuiSpeedDial-directionUp, &.MuiSpeedDial-directionLeft': {
        bottom: theme.spacing(2),
        right: theme.spacing(2),
      },
      '&.MuiSpeedDial-directionDown, &.MuiSpeedDial-directionRight': {
        top: theme.spacing(2),
        left: theme.spacing(2),
      },
    },
    xxx: {
      display: "fixed",
      right: "50px",
      bottom: "20px",
      '& > *': {
        margin: theme.spacing(1),
      },
    }
  }),
);

const StyledBadge = withStyles((theme: Theme) =>
  createStyles({
    badge: {
      backgroundColor: '#44b700',
      color: '#44b700',
      boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
      '&::after': {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        borderRadius: '50%',
        animation: '$ripple 1.2s infinite ease-in-out',
        border: '1px solid currentColor',
        content: '""',
      },
    },
    '@keyframes ripple': {
      '0%': {
        transform: 'scale(.8)',
        opacity: 1,
      },
      '100%': {
        transform: 'scale(2.4)',
        opacity: 0,
      },
    },
  }),
)(Badge);



export default function PositionedPopper() {
  const [anchorEl, setAnchorEl] = React.useState<HTMLButtonElement | null>(null);
  const [open, setOpen] = React.useState(false);
  const [placement, setPlacement] = React.useState<PopperPlacementType>();
  const [online, setOnline] = React.useState<boolean>(true)
  const classes = useStyles();

  const handleClick = (newPlacement: PopperPlacementType) => (
    event: React.MouseEvent<HTMLButtonElement>,
  ) => {
    setAnchorEl(event.currentTarget);
    setOpen(true)
    setPlacement('top-end');
  };

  const onCloseChat = () => {
    setOpen(false)
  }

  const arrChat = [
    {
      message: "Xin chào tôi có thể giúp đỡ gì cho bạn",
      date: "1586164019224",
      type: "admin"
    },
    {
      message: "Làm ơn cho tôi xin thêm thông tin về dự án sắp mở bán của các bạn trong khu đô thị Đại Thanh",
      date: "1586183019224",
      type: "user"
    },
    {
      message: "Rất sẵn lòng bạn muốn biết thông tin về giá cả, mặt bằng, diện tích đất nền ...",
      date: "1586184012224",
      type: "admin"
    },
    {
      message: "Xin hãy cho tôi biết diện tích mặt sàn, giá bán , phí môi giới ... ",
      date: "1586184049224",
      type: "user"
    }
  ]

  console.log("sizeAvatarSmall", new Date().getTime())

  const viewChat = () => {
    return (
      <Popper 
        open={open} 
        anchorEl={anchorEl}
        placement={placement} 
        transition
      >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={300}>
            <Paper>
              <div className={classes.positionSizeViewChat} >
                <CardHeader
                  className={classes.hearder}
                  avatar={
                    <StyledBadge
                      overlap="circle"
                      anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                      variant= { online ? "dot" : "standard" }
                    >
                      <Avatar 
                        className={classes.sizeAvatarSmall} 
                        alt="nhadatmoi.net" 
                        src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
                      />
                    </StyledBadge>
                  }
                  title="Nhà đất mới"
                  action={
                    <IconButton color='inherit' aria-label="settings" onClick={() => onCloseChat()} >
                      <CloseIcon/>
                    </IconButton>
                  }
                />
                <CardContent>
                  <Typography align="center">
                    {moment().format('LLL')}
                  </Typography>
                  <Divider />
                  {arrChat.map((item: any, index: number) => {
                    return (
                     <div>
                        { item.type === 'admin' ?
                          <Typography className={classes.textAdmin}>
                            {item.message}
                          </Typography> :
                          <Typography className={classes.textUser}>
                            {item.message}
                          </Typography> 
                        }
                     </div>
                    )
                  })}
                </CardContent>
                <Paper component="form" className={classes.inputBase}>
                  {/* <InputBase
                    className={classes.input}
                    placeholder="Nhập tin nhắn ..."
                    // inputProps={{ 'aria-label': 'search google maps' }}
                  /> */}
                  <ThemeProvider theme={theme}>
                    <Input 
                      fullWidth
                      margin="dense"
                      id="outlined-basic" 
                      label="" 
                      multiline={true}
                      placeholder="Nhập tin nhắn ..."
                      variant="outlined" 
                      autoComplete="false"
                      endAdornment={<InputAdornment position="end">
                          <Tooltip title="Chèn biểu tượng" placement="top">
                            <TagFacesIcon 
                              style={{ fontSize: "30px" }} 
                              color="primary"
                            />
                          </Tooltip>
                          <Tooltip title="Thêm tập tin" placement="top">
                            <AddCircleOutlineIcon style={{ fontSize: "30px" }} color="primary"/>
                          </Tooltip>
                      </InputAdornment>}
                      InputProps={{
                        startAdornment: <InputAdornment position="end">
                          
                        </InputAdornment>,
                      }}
                    />
                  </ThemeProvider>
                  
                  
                </Paper>
              </div>
            </Paper>
          </Fade>
        )}
      </Popper>
    )
  }

  const onClickDiv = () => {
    setOpen(true)
    setPlacement('top-end');
  }

  const renderAvatar = () => {
    return (
      <div className={classes.positionAvatar} onClick={() => onClickDiv()}>
        <StyledBadge
          overlap="circle"
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'right',
          }}
          variant= { online ? "dot" : "standard" }
        >
          <Avatar 
            className={classes.large} 
            alt="nhadatmoi.net" 
            src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
          />
        </StyledBadge>
      </div>
    )
  }

  const renderIcon = () => {
    return (
      <div className={classes.exampleWrapper}>
        <SpeedDial
          ariaLabel="SpeedDial example"
          className={classes.speedDial}
          icon={<SpeedDialIcon onClick={handleClick('top-end')} />}
          open={false}
        /> 
      </div>
    )
  }

  return (
    <div className={classes.root}>
      {viewChat()}
      <Grid container justify="center">
        <Grid item>
          {/* {renderIcon()} */}
          
          { !open ? renderAvatar() : ""}
        </Grid>
      </Grid>
    </div>
  );
}
