import React from "react";
import clsx from "clsx";
import { Grid, Typography, Card, CardContent, CardActions } from "@material-ui/core";
import { createStyles, Theme, makeStyles } from "@material-ui/core/styles";
import { List, Avatar } from "@material-ui/core";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import WcIcon from "@material-ui/icons/Wc";
import PhoneIcon from "@material-ui/icons/Phone";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import EmailIcon from "@material-ui/icons/Email";
import PhoneInTalkIcon from '@material-ui/icons/PhoneInTalk';
import { member, memberStyles} from "../Constant"
import { GET_MEMBER_CHANNEL_BY_ID } from "../../../graphql/channel/queries"
import { apolloClient } from "../../../utils";

interface InfoProps {

}

interface User {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  id: string;
  intro: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

interface Member {
  node: {
    avatar: string
    first_name: string
    last_name: string
    mobile: string
  }
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
  const classes = memberStyles();
  const [members, getMembers] = React.useState<Member[]>([])

  const getMemberChannel = async ()=> { 
    let id = "5eb0f1c5e20716472e9fb50f"
    let members = await apolloClient.query({ query: GET_MEMBER_CHANNEL_BY_ID, variables: {
      "id": id,
      "limit": 18,
      "page": 1,
    }})
    .then((response: any) => {
      return response.data.channelMembers.edges
    })
    if(Array.isArray(members)){
      getMembers(members)
    }
  };

  React.useEffect(() => {
    getMemberChannel()
  })

  return (
    <div className={classes.rootMember}>
      <Typography variant="h5" className={classes.titleMember}>Danh sách môi giới</Typography>
      <Grid container direction="row" alignItems="center" spacing={1}>
        {member.map((item: any, index: number) => {
          return (
            <Grid key={index} item lg={2} md={3} sm={4} xs={6}  style={{ margin: "10px 0px"}}>
              <div className={classes.members} >
                <Avatar alt="" className={classes.avatar} src={item.avatar} />
                <div style={{ height: "45px" }}>
                  <Typography style={{ color: "black" }} >{item.name}</Typography>
                </div>
                <a title="Gọi ngay" href="tel:0936162822">
                  <PhoneInTalkIcon />
                </a>
                <Typography>{item.phone}</Typography>
              </div>
            </Grid>
          );
        })}
      </Grid>
    </div>
  );
};

export default Info;
