import React from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField, Button, Icon, IconButton} from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import {apolloClient} from "../../../utils"
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import {GET_AD_SELL_LEASE_COMMENTS} from "../../../graphql/comments/queries";
import {GET_USER_BY_ID } from "../../../graphql/user/queries/index"
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

const useStyles = makeStyles(theme => ({
  root: {
  },
  iconUp: {
    fontSize: "10",
  },
  subheader: {
    flexWrap: 'wrap',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

interface ListReview {
  listComment: { data: { adSellLeaseComments: { edges: ListCommentProps[]}} },
}

interface ListCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface OverallReviewsProps {
  className?:string,
  listComment: ListCommentProps[],
  userId: string
} 

interface Review {
  id: string,
  reviewer: {
    avatar: string,
    name: string,
  },
  rating: number,
  created_at: string,
  message: string
}

const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  const [value, setValue] = React.useState<number | null>(3)
  const [rating, setRating] = React.useState<number | null>(0)
  const [message, setMessage] = React.useState<string>("")
  const [reviewLocal, setReviewLocal] = React.useState<ListCommentProps[]>([])

  const [indexView, setViews] = React.useState<number>(0)
  const [page, setPage] = React.useState<number>(2)
  const [moreReview, setMoveReview] = React.useState<ListReview[]>([])
  const setViewPage = () => {
    let filterObject =  { id: props.userId }
    let dataFilter = JSON.stringify(filterObject);
    apolloClient.query({query: GET_AD_SELL_LEASE_COMMENTS, variables: {
        "page": 2,
        "limit": 15,
        "filter": dataFilter
      }}).then(response => {
        setMoveReview(response.data.adSellLeaseComments.edges)
    });
  
    setPage(page + 1)
  }

  console.log("moreReview", moreReview)
  const setReview = (e: any, name: string) => {
    let item = {
      id: `${new Date().getTime()}`,
      reviewer: {
        avatar: "https://caygiongnongnghiep.com/wp-content/uploads/2017/08/cay-hoa-hong-doi-mau.jpg",
        name: "Minh Quang"
      },
      rating: rating,
      created_at: moment().format("DD MM YYYY"),
      message: message,
    }
    if(name === "buttonSen"){
      addReview(item) 
      setMessage("")
      setRating(0)
    }
    if (name === "textField" && e.key == "Enter" || e.keyCode == 13) {
      addReview(item) 
      setMessage("")
      setRating(0)
    }
  }

  const addReview = (item: any) => setReviewLocal(state => [...reviewLocal, item])
  const { className } = props;
  const classes = useStyles();
   
  return (
    <div  className={clsx(classes.root, className)}>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Card className={clsx(classes.root, className)}>
            <CardContent>
              <Grid alignItems="center" container spacing={3}>
                <Grid item>
                  <Typography color="textSecondary" variant="body2" >
                    {props.listComment.length} lượt bình luận
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      {props.listComment.map((item: any, index: number) => {
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={item.node.user.avatar} >
                      {item.node.user.first_name}  {item.node.user.last_name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      <Typography variant="body2">
                        Bình luận bởi{' '} <b>{item.node.user.first_name} {item.node.user.last_name} </b> 
                      </Typography>
                      <Typography variant="body2">
                        {moment(Number(item.node.created_at)).fromNow()}
                      </Typography>
                      {/* <Typography variant="body2">
                        Dự án :  
                      </Typography> */}
                    </div>
                  }
                />
                <div className={classes.message}>
                  <Typography variant="subtitle2">{item.node.content}</Typography>
                </div>
              </Card>
            </Grid>
          </Grid>
        )  
      })}

      <Grid container spacing={4}>
        <Grid item>
          <Button onClick={setViewPage} >Hiển thị thêm</Button>
        </Grid>
      </Grid>
    </div>
  );
};


export default Project;
