import React from "react";
import clsx from "clsx";
import { Grid, Typography, Card, CardContent, CardActions } from "@material-ui/core";
import { createStyles, Theme, makeStyles } from "@material-ui/core/styles";
import { List, Avatar } from "@material-ui/core";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import WcIcon from "@material-ui/icons/Wc";
import PhoneIcon from "@material-ui/icons/Phone";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import EmailIcon from "@material-ui/icons/Email";
import PhoneInTalkIcon from '@material-ui/icons/PhoneInTalk';
import { member, memberStyles} from "../Constant"
import { GET_MEMBER_CHANNEL_BY_ID } from "../../../graphql/channel/queries"
import { apolloClient } from "../../../utils";

interface InfoProps {
  channelId: string
}

interface User {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  id: string;
  intro: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

interface Member {
  node: {
    avatar: string
    first_name: string
    last_name: string
    mobile: string
    phone: string
  }
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
  const classes = memberStyles();
  const [members, getMembers] = React.useState<Member[]>([])

  const getMemberChannel = async ()=> { 
    let id = props.channelId
    let members = await apolloClient.query({ query: GET_MEMBER_CHANNEL_BY_ID, variables: {
      "id": id,
      "limit": 18,
      "page": 1,
    }})
    .then((response: any) => {
      return response.data.channelMembers.edges
    })
    if(Array.isArray(members)){
      getMembers(members)
    }
  };

  React.useEffect(() => {
    getMemberChannel()
  })

  const renderMember = () => {
    return (
      <Grid container direction="row" alignItems="center" spacing={1}>
        {member.map((item: any, index: number) => {
          return (
            <Grid key={index} item lg={2} md={3} sm={4} xs={6} className={classes.marginMember}>
              <div className={classes.members} >
                <Avatar alt="" className={classes.avatar} src={item.avatar} />
                <div className={classes.nameMember}>
                  <Typography>{item.name}</Typography>
                </div>
                <a title="Gọi ngay" href="tel:0936162822">
                  <PhoneInTalkIcon />
                </a>
                <Typography>{item.phone}</Typography>
              </div>
            </Grid>
          );
        })}
      </Grid>
    )
  }

  const renderMembers = () => {
    return (
      <Grid container direction="row" alignItems="center" spacing={1}>
        {members.map((item: any, index: number) => {
          let phone = item.node.mobile || item.node.phone
          return (
            <Grid key={index} item lg={2} md={3} sm={4} xs={6} className={classes.marginMember}>
              <div className={classes.members} >
                <Avatar alt="" className={classes.avatar} src={item.node.avatar} />
                <div className={classes.nameMember}>
                  <Typography>{item.node.first_name} {item.node.last_name}</Typography>
                </div>
                <a title="Gọi ngay" href={`${phone}`}>
                  <PhoneInTalkIcon />
                </a>
                <Typography>{phone ? phone : "SĐT: Đang cập nhật ... " }</Typography>
              </div>
            </Grid>
          );
        })}
      </Grid>
    )
  }

  return (
    <div className={classes.rootMember}>
      <Typography variant="h5" className={classes.titleMember}>Danh sách môi giới</Typography>
        {renderMembers()}
        {/* {renderMember()} */}
    </div>
  );
};

export default Info;
