import React, { FunctionComponent, useState} from 'react';
// import {Redirect, BrowserRouter as Router, useHistory, useParams} from "react-router-dom"
import {makeStyles} from '@material-ui/core/styles'
import Timeline from './Tabs/Timeline'
import Projects from "./Tabs/Projects"
import Ad from "./Tabs/Ad"
import Header from "./Header"
import Paper from '@material-ui/core/Paper';
import {Tabs, Tab, Divider, colors} from '@material-ui/core';
import PhoneIcon from '@material-ui/icons/Phone';
import {apolloClient} from "../../utils";
import {GET_PROJECT_LIST_NAME} from "../../graphql/project/queries";
import {GET_AD_SELL_LEASES} from "../../graphql/ad_sell_leases/queries"
import Page from "./Page";

// import {
  // Header, 
  // Timeline, 
  // Connections, 
  // Projects, 
  // Reviews
// } from './components';

const useStyles = makeStyles(theme => ({
  root: {},
  rootTabs: {
    flexGrow: 1,
  },
  inner: {
      width: theme.breakpoints.values.lg,
      maxWidth: '100%',
      margin: '0 auto',
      padding: theme.spacing(3)
  },
  divider: {
      backgroundColor: colors.grey[300]
  },
  content: {
      marginTop: theme.spacing(3)
  }
}));

interface ProfileProps {

}


interface AdProps  {
  edges: {
    node: {
      name: string,
      id: string
    }
  }[],
}



interface ParamType {
  id:any,
  tab:any
}


const Index: FunctionComponent<ProfileProps> = (props: ProfileProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = React.useState("timeline")
  const [user, setUser] = React.useState<AdProps>();
  const tabs = [
    {value: 'timeline', label: 'Timeline'},
    {value: 'projects', label: 'Dự án'},
    {value: 'ad', label: 'Tin rao'}
  ]
  
  React.useEffect(() => {
    let userId = { user: ""}
    let filterUser = JSON.stringify(userId)
    apolloClient.query({query: GET_AD_SELL_LEASES, variables: {
      "page": 1,
      "limit": 10,
      "filter": filterUser 
    }}).then(response => { 
      setUser(response.data.adSellLeases.edges)
    })
  }, [])

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value)
  }
  console.log("user >>>> ", user)
  return (
    <div>
      <Page title="Profile">
        <Header/>
        <div className={classes.inner}>
          <Paper square className={classes.rootTabs}>
            <Tabs
              onChange={handleTabsChange}
              scrollButtons="auto"
              indicatorColor="primary"
              textColor="primary"
              value={itemTab}
              centered
              variant="fullWidth"
            >
              {tabs.map(tab => (
                <Tab key={tab.value} label={tab.label} value={tab.value} />
              ))}
            </Tabs>
          </Paper>
          <Divider className={classes.divider}/>
          <div className={classes.content}>
              {itemTab === 'timeline' && <Timeline/>}
              {itemTab === 'projects' && <Projects/>}
              {itemTab === 'ad' && <Ad/>}
          </div>
        </div>
      </Page>
    </div>
  )
}


export default Index
