  image: string
  name: string
  address: string
  mobile: string
  phone: string,
  email: string
}

interface DetailInvestorProp {
  id: string
  name: string
  description: string
  slug: string
  email: string
  website: string
  image: string
}
 

interface ProjectDetailsProp {
  contact: ContactProp
  detailInvestor: DetailInvestorProp
}

const starIconEnable = (
  <svg viewBox="0 0 576 512" className="star_icon_enable">
    <path d="M259.3 17.8L194 150.2 47.9 171.5c-26.2 3.8-36.7 36.1-17.7 54.6l105.7 103-25 145.5c-4.5 26.3 23.2 46 46.4 33.7L288 439.6l130.7 68.7c23.2 12.2 50.9-7.4 46.4-33.7l-25-145.5 105.7-103c19-18.5 8.5-50.8-17.7-54.6L382 150.2 316.7 17.8c-11.7-23.6-45.6-23.9-57.4 0z"/>
  </svg>
);
const starIconDisable = (
  <svg viewBox="0 0 576 512" className="star_icon_disable">
    <path d="M259.3 17.8L194 150.2 47.9 171.5c-26.2 3.8-36.7 36.1-17.7 54.6l105.7 103-25 145.5c-4.5 26.3 23.2 46 46.4 33.7L288 439.6l130.7 68.7c23.2 12.2 50.9-7.4 46.4-33.7l-25-145.5 105.7-103c19-18.5 8.5-50.8-17.7-54.6L382 150.2 316.7 17.8c-11.7-23.6-45.6-23.9-57.4 0z"/>
  </svg>
);

const ContactArea: FunctionComponent<ProjectDetailsProp> = (props: ProjectDetailsProp) => {
  let brokerAvatar = UrlProcess.processImageUrl(props.contact.image, 'broker');
  let statusUserDescription = false;

  let phoneNumber = 'tel:' + props.contact.phone;
  //Phone Number and Email
  if (!props.contact.phone) {
    phoneNumber = 'tel:' + props.contact.mobile;
  }
  let mailTo = 'mailto:' + props.contact.email;
  let detailInvestor = props.detailInvestor
  console.log("detailInvestor >>>>>> ", detailInvestor)
  let description = props.detailInvestor.description
  // name, description, email, website, image, 
  return (
    <div> 
      {/* className="contact-area__title" */}
      <h1 className="title-project__title">
        Chủ đầu tư 
      </h1>
      <img src={props.detailInvestor.image} alt="Nhatdatmoi" />
      <h2 className="project-info-title">
        {props.detailInvestor.name}
      </h2>        
      <div style={{ textAlign: "center", color: 'blue' }} >
        {props.detailInvestor.website}
      </div>
      <div>
        {props.detailInvestor.email}
      </div>
     
    </div>
  );
};

export default ContactArea;
