import React, {FunctionComponent} from "react"
import { Grid, Button, Icon, IconButton, Typography} from "@material-ui/core"
import StringProcess from '../../modules/stringProcess';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import SmartphoneIcon from '@material-ui/icons/Smartphone';
import LocalPhoneIcon from '@material-ui/icons/LocalPhone';
import WebAssetIcon from '@material-ui/icons/WebAsset';
import WebIcon from '@material-ui/icons/Web';
import EmailIcon from '@material-ui/icons/Email';
import PhoneIphone from "@material-ui/icons/PhoneIphone";

interface InvestorProp {
  id: string
  name: string
  description: string
  slug: string
  email: string
  website: string
  image: string
  address: {
   text: string
  }
  phone: string
  mobile: string
}
 
interface ProjectDetailsProp {
  investor: InvestorProp
}

const DetailInvestor: FunctionComponent<ProjectDetailsProp> = (props: ProjectDetailsProp) => {
  const freshText = (input: string) => {
    input = StringProcess.stripProperties(input, ['href', 'src', 'rel']);
    input = StringProcess.stripTags(input, '<a><br/><br><img><p><h1><h2><h3><h4><h5><u><img><video><audio><li><ul><table><tr><td><sup><sub>');
    input = input.replace(/https:\/\/nhadatmoi.net\/data/g, 'https://static.nhadatmoi.net/data');
    input = input.replace(/&nbsp;/g, ' ');
    return input;
  };

  let description = freshText(String(props.investor.description));
  return (
    <Grid container spacing={4}>
      <Grid 
        item xs={12} 
        className="investor_homepage"
        style={{
          // backgroundImage: 'url(' + props.investor.image + ')'
          backgroundImage: "url('https://bietthubiennhatrang.net/wp-content/uploads/2019/07/tien-do-xay-dung-du-an-movenpick-cam-ranh-thang-7-2019-6.jpg')"
        }} 
        direction="row"
        justify="center"
        alignItems="center"
      >
      </Grid>
      <Grid item xs={1}></Grid>
      <Grid item xs={3} className="footer_body__item border_bottom_mobile">
        <div style={{ position: "sticky", top: "80px" }} >
          <h3 className="project-info-title text-active u-margin-bottom-small">Thông tin chung</h3>
          
        
        <div className="footer_body__item_description">
          <div className="footer_body__item_description_icon">
            <svg viewBox="0 0 384 512">
              <path
                d="M172.268 501.67C26.97 291.031 0 269.413 0 192 0 85.961 85.961 0 192 0s192 85.961 192 192c0 77.413-26.97 99.031-172.268 309.67-9.535 13.774-29.93 13.773-39.464 0zM192 272c44.183 0 80-35.817 80-80s-35.817-80-80-80-80 35.817-80 80 35.817 80 80 80z"/>
            </svg>
          </div>
          <div className="footer_body__item_description_text">
            <p className="footer_body__item_description_text_title">Địa chỉ</p>
            <p className="footer_body__item_description_text_content">
              Số 348 Tố Hữu, phường La Khê, quận Hà Đông, TP. Hà Nội
            </p>
          </div>
        </div>
        <div className="footer_body__item_description">
          <div className="footer_body__item_description_icon">
            <PhoneIphone/>
          </div>
          <div className="footer_body__item_description_text">
            <p className="footer_body__item_description_text_title">Điện thoại</p>
            <p className="footer_body__item_description_text_content">
              0377 535 717
            </p>
          </div>

        </div>
        
        <div className="footer_body__item_branch">
          <img className="gov" src="https://static.nhadatmoi.net/default/bocongthuong.png"
               alt="Đã thông báo với Bộ Công Thương"/>
          <a href={'https://www.dmca.com/Protection/Status.aspx?ID=7816605d-fbd2-45f8-a397-eeac42642b75&refurl=https://nhadatmoi.net/'} target={'_blank'} rel={'nofollow'} >
            <img src="https://static.nhadatmoi.net/default/dmca.png" alt="DMCA" className="dmca"/>
          </a>
        </div>
      
          <Typography>
            <b>Địa chỉ: </b>{props.investor.address.text ? props.investor.address.text : "Đang cập nhật"}
          </Typography>
          <Typography>
            <b>Email: </b>{props.investor.email ? props.investor.email : "Đang cập nhật"}
          </Typography>
          <Typography>
            <b>Số điện thoại: </b>{props.investor.phone ? props.investor.phone : 'Đang cập nhật'}
          </Typography>
          <Typography>
            <b>Số di động: </b>{props.investor.mobile ? props.investor.mobile : "Đang cập nhật"}
          </Typography> 
          <Typography><b>Website: </b>
            { props.investor.website 
            ? <a href={`${props.investor.website}`} target="_blank">{props.investor.website}</a>
            : "Đang cập nhật"}
          </Typography>
        </div>
      </Grid>
      <Grid item xs={7}>
        <Grid container spacing={2}>
          <Grid item xs={3} >
            <div style={{ width: "150px", height: "150px" }}>
              <img 
                src={props.investor.image} 
                alt="nhadatmoi.net" 
                style={{ width: "100%", height: "100%", borderRadius: "50%", border: "5px solid #1A4684" }} 
              />
            </div>
          </Grid>
          <Grid item xs={8}>
            <h1 className="title-project__title">{props.investor.name}</h1>
          </Grid>
        </Grid>
        {
          props.investor.description &&
            <div 
              className="project-info__description u-margin-bottom-medium" 
              dangerouslySetInnerHTML={{ __html: description }} 
            />
        }
      </Grid>
      <Grid item xs={1}></Grid>
    </Grid>
  )
}

export default DetailInvestor
