export default function PositionedPopper() {
  const [anchorEl, setAnchorEl] = React.useState<HTMLButtonElement | null>(null);
  const [open, setOpen] = React.useState(false);
  const [placement, setPlacement] = React.useState<PopperPlacementType>();
  const [online, setOnline] = React.useState<boolean>(true)
  const [internet, setInternet] = React.useState<boolean>(false)
  const classes = useStyles();
  const [texting, setTexting] = React.useState<boolean>(false)
  const [valueInput, setValueInput] = React.useState("")
  
  let url = "https://api.giphy.com/v1/gifs/search?api_key=UgTMyvovmg74m8fPN6qYiB623nwS1IPc&q=dog&limit=100&offset=0&rating=G&lang=en"
  

  const getData = ()=>  { 
    axios.get('url', { params: { ID: 12345 } })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    })
      // .finally(function () {
      //   // always executed
      // });  
  }
  
   
  
  React.useEffect(() => {
    (async () => {
      
      // const [err, response] = await to(salary.getByMonth({ month: moment().format('YYYY-MM') }))
      // let getData_ = await axios.get('https://api.giphy.com/v1/gifs/search?api_key=UgTMyvovmg74m8fPN6qYiB623nwS1IPc&q=dog&limit=100&offset=0&rating=G&lang=en'))
      // setSalaryData(response.data)
    })()
    getData()
  })

  const handleClick = (newPlacement: PopperPlacementType) => (
    event: React.MouseEvent<HTMLButtonElement>,
  ) => {
    setAnchorEl(event.currentTarget);
    setOpen(true)
    setPlacement('top-end');
  };

  const onCloseChat = () => {
    setOpen(false)
  }

  const setOnKeyPress = (event: any) => {
    setTexting(true)
  }
  
  const setOnBlur = (event: any) => {
    setTexting(true)
  }

  const setOnKeyDown = (event: any) => {
    if(event.keyCode == 13){
      arrChat.push({ message: event.target.value, type: 'user', date: String(new Date().getTime()) })
      setValueInput("")
    }
  }

  const senMesssage = () => {
    arrChat.push({ message: valueInput, type: 'user', date: String(new Date().getTime()) })
    setValueInput("")
  }


  const renderHeader = () => {
    return (
      <CardHeader
        className={classes.hearder}
        avatar={
          <StyledBadge
            overlap="circle"
            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            variant= { online ? "dot" : "standard" }
          >
            <Avatar 
              className={classes.sizeAvatarSmall} 
              alt="nhadatmoi.net" 
              src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
            />
          </StyledBadge>
        }
        title="Nguyễn Thanh Huyền"
        action={
          <IconButton color='inherit' aria-label="settings" onClick={() => onCloseChat()} >
            <CloseIcon/>
          </IconButton>
        }
      />
    )
  }

  const renderContentChat = () => {
    return (
      <div style={{ overflowY: "auto", padding: "8px", height: "430px" }}>
        <Typography align="center" className={classes.textSizeNewDate}>
          {moment().format('LLL')}
        </Typography>
        <Divider />
        {arrChat.map((item: any, index: number) => {
          let checkNewDate = false
          if(index > 0 && (moment(Number(arrChat[index - 1].date)).format('YYYY-MM-DD') != moment(Number(arrChat[index].date)).format('YYYY-MM-DD'))) {
            checkNewDate = true
          }
          let setViewAvatar = false
          if(item.type === 'admin' && index > 0 && (index === arrChat.length || arrChat[index - 1].type != arrChat[index].type)) {
            setViewAvatar =  true
          }
          return (
            <div>
              { checkNewDate && <Typography align="center" className={classes.textSizeNewDate}>
                {moment(Number(item.date)).format("LLL")} <Divider /></Typography>}
              { item.type === 'admin' 
              ? <ThemeProvider theme={themeTextfieldAdmin}>
                  <div style={{ float: "left", marginTop: "10px" }}>
                    {
                      setViewAvatar 
                      ? <Avatar 
                          className={classes.avatarAdminText} 
                          alt="nhadatmoi.net" 
                          src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
                        />
                      : ""
                    }
                  </div>
                  <div style={{ marginLeft: "30px"}}>
                    <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                      <TextField 
                        fullWidth
                        label=""
                        className={classes.textAdmin}
                        value={item.message}
                        margin="dense"
                        id="outlined-basic" 
                        multiline={true}
                        InputProps={{
                          readOnly: true
                        }}
                        variant="outlined" 
                      />
                    </Tooltip>
                  </div>
                  </ThemeProvider>
              : <ThemeProvider theme={themeTextfieldUser}>
                  <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                    <div style={{ marginLeft: "30px", padding: '5px', borderRadius: "16px!important" }}>
                      <TextField 
                        fullWidth
                        label=""
                        className={classes.textUser}
                        value={item.message}
                        margin="dense"
                        id="outlined-basic" 
                        multiline={true}
                        InputProps={{
                          readOnly: true
                        }}
                        variant="outlined" 
                      /> 
                    </div>
                  </Tooltip>
                  </ThemeProvider>
              }
              
            </div>
          )
        })}
        {!internet 
          ? <p className={classes.errInternet}>
              <u>Không thể gửi tin nhắn này. Kiểm tra kết nối internet và thử lại</u>
            </p>
          : ""
        }
        {texting && <p className={classes.texting}>Đang nhập ...</p>}
      </div>
    )
  }

  const setChangeInput = (value: any) => {
    
    setValueInput(value)
  }

  const renderInput = () => {
    return (
      <Paper component="form" className={classes.inputBase}>
        <ThemeProvider theme={theme}>
          <Input 
            fullWidth
            // disabled={internet}
            margin="dense"
            id="outlined-basic" 
            autoFocus={true}
            value={valueInput}
            multiline={true}
            onChange={(e) => setChangeInput(e.target.value)}
            onKeyPress={(e) => setOnKeyPress(e)}
            onBlur={(e) => setOnBlur(e)}
            onKeyDown={(e) => setOnKeyDown(e)}
            placeholder="Nhập tin nhắn ..."
            autoComplete="false"
            endAdornment={<InputAdornment position="end">
              <Tooltip title="Chèn biểu tượng" placement="top">
                <TagFacesIcon 
                  className={classes.sizeIcon}
                  color="primary"
                />
              </Tooltip>
              {
                valueInput
                ? <Tooltip title="Thêm tập tin" placement="top">
                    <SendIcon onClick={senMesssage} className={classes.sizeIcon} color="primary"/>
                  </Tooltip>
                : <Tooltip title="Thêm tập tin" placement="top">
                    <AddCircleOutlineIcon className={classes.sizeIcon} color="primary"/>
                  </Tooltip>
              }
            </InputAdornment>}
          />
        </ThemeProvider>
      </Paper>
    )
  }

  const viewChat = () => {
    return (
      <Popper 
        open={open} 
        anchorEl={anchorEl}
        placement={placement} 
        transition
      >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={300}>
            <Paper>
              <div className={classes.positionSizeViewChat} >
                {renderHeader()}
                {renderContentChat()}
                {renderInput()}
              </div>
            </Paper>
          </Fade>
        )}
      </Popper>
    )
  }

  const onClickDiv = () => {
    setOpen(true)
    setPlacement('top-end');
  }

  const renderAvatar = () => {
    return (
      <div className={classes.positionAvatar} onClick={() => onClickDiv()}>
        <StyledBadge
          overlap="circle"
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'right',
          }}
          variant= { online ? "dot" : "standard" }
        >
          <Avatar 
            className={classes.large} 
            alt="nhadatmoi.net" 
            src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
          />
        </StyledBadge>
      </div>
    )
  }

  return (
    <div className={classes.root}>
      {viewChat()}
      <Grid container justify="center">
        <Grid item>
          { !open && renderAvatar()}
        </Grid>
      </Grid>
    </div>
  );
}
