import React, {FunctionComponent} from "react";
import PageDetailInfo from "../PageDetail/PageDetailInfo";
//Add Page Detail
import TitleProject from "../PageDetail/TitleProject";
import ContactArea from "../PageDetail/ContactArea";
import Investor from "../PageDetail/Investor"
import AlbumImage from "../PageDetail/AlbumImage";
import AdSellLeaseProject from "./AdSellLeaseProject";

//Add Comment
import CommentArea from "../Comment/Comment";

// Material - UI
import Grid from "@material-ui/core/Grid";

interface ProjectMainProps {
  propertyOverview: {
    id: string,
    building_density: string|null,
    type: string|null,
    status: string|null,
    floor_number: number|null,
    living_room_number: number|null,
    bed_room_number: number|null,
    toilet_room_number: number|null,
    gara_number: number|null,
    material: string|null,
    length: string|null,
    width: string|null,
    price: string|null,
    area: any,
    year_built: string|null,
    address: {
      text: string
    },
    size: string|null,
    description: string|null,
    map: {
      lng: string,
      lat: string
    },
    view_count: number,
    title: string|null,
    updated_at: string|null
    created_at: string|null
    position_content: string|null
    infrastructure_content: string|null
  },
  block: {
    image: ImageProp
  },
  investor: InvestorProp,
  contact: ContactProp,
  images: ImageProp[],
  imageType: string,
  objectUrl: string,
  avatar: string,
  updateCompareData: (data: CompareDataProps) => void;
}

interface ContactProp {
  image: string
  name: string
  address: string
  mobile: string
  phone: string
  email: string
}

interface InvestorProp {
  id: string
  name: string
  description: string
  slug: string
  email: string
  website: string
  image: string
  address: {
    text: string
  },
  phone: string
  mobile: string
}

interface CompareDataProps {
  avatar: string,
  title: string|null,
  id: string,
  type: string
}
interface ImageProp {
  alt_text: string
  description: string
  title: string
  src: string
}

const ProjectMain: FunctionComponent<ProjectMainProps> = (props: ProjectMainProps) => {
  return (
    <div className="container-big">
      <Grid container spacing={3}>
        <Grid item className="project-detail__left">
          <TitleProject
            propertyOverview={props.propertyOverview}
            objectUrl={props.objectUrl}
            avatar={props.avatar}
            type={'project'}
            updateCompareData={(data: CompareDataProps) => props.updateCompareData(data)}
          />
          <PageDetailInfo 
            block={props.block}
            propertyOverview={props.propertyOverview} 
            contact={props.contact}
          />
          <AlbumImage 
            images={props.images} 
            imageType={props.imageType}
          />
          <AdSellLeaseProject id={props.propertyOverview.id}/>
          <CommentArea/>
        </Grid>
        <Grid item className="project-detail__right">
          {/* <div className="contact-area">
            <Investor investor={props.investor} contact={props.contact}/>
          </div> */}
          <div className="investor-area">
            <Investor investor={props.investor} contact={props.contact}/>
          </div>
        </Grid>
      </Grid>
    </div>
  );
};

export default ProjectMain;
